var ydata = [1, 1, 1, 1, 1, 2, 2, 2, 1, 1, 2, 4, 3, 4, 1, 1, 1, 1, 2, 2, 2, 3, 2, 2, 2, 1, 1, 1, 1, 1]
var xData = function() {
    var data = [];
    for (var i = 1; i < 31; i++) {
        data.push(i + "日");
    }
    return data;
}();

option = {
    backgroundColor: "#1A1835",

    tooltip: {
        trigger: "axis",
        axisPointer: {
            type: "shadow",
            textStyle: {
                color: "#fff"
            }

        },
    },
    grid: {
        borderWidth: 0,
        top: 110,
        bottom: 95,
        textStyle: {
            color: "#fff"
        }
    },
    calculable: true,
    xAxis: [{
        type: "category",
        axisLine: {
            show: false
        },
        splitLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            show: false
        },
        data: xData,
    }],

    yAxis: [{
        type: "value",
        max: 20,
        min: -20,
        splitLine: {
            show: false
        },
        axisLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            show: false
        }
    }],
    series: [{
        name: "心电图",
        type: "line",
        showAllSymbol: false,
        symbolSize: 50,
        symbol: 'image://data:image/png;base64,',
        lineStyle: {
            color: "#37C4D1",
            shadowColor: "rgba(55,196,209,0.9)",
            shadowBlur: 15,
            shadowOffsetY: 0
        },
        itemStyle: {
            borderWidth: 5,
            color: "#F98F02",
        },
        markPoint: {
            symbol: 'circle',
            symbolSize: 9,
            label: {
                show: false,
                normal: {
                    textStyle: {
                        color: 'rgba(0,0,0,0)'
                    }
                }
            },
            itemStyle: {
                color: "#F98F02",
                shadowColor: "rgba(249,143,2,0.8)",
                shadowBlur: 10,
                shadowOffsetY: 0
            },
            data: [{
                type: 'max',
                name: '最大值',

            }]
        },
        data: ydata,
    }]
}
 function randomData() {
     var value = Math.random() * 2;

     value = value + Math.random() * 2;

     return Math.round(value)

 }
 setInterval(function() {
     ydata.shift();

     ydata.push(randomData());
     myChart.hideLoading();

     myChart.setOption(option);

 }, 2000);
