let chartData =[
    {"value": 600,"name": "单位1"},
    {"value":500,"name": "单位2"},
    {"value":400,"name": "单位3"},
    {"value": 300,"name": "单位4"},
    {"value": 200,"name": "单位5"},
];

let icon1 = 'data:image/png;base64,';
let icon2='data:image/png;base64,';

let colorList  =[
    ['#9ffafa', '#9ffafa', '#9ffafa', '#0093ff'],
    ['#f4ceb9', '#f4ceb9', '#f4ceb9', '#0093ff'],
    ['#b2b4ff', '#b2b4ff', '#b2b4ff', '#0093ff']
]

option = {
    backgroundColor:'#003a82',
    tooltip: {
                    show:false,
                    trigger: 'axis',
                    axisPointer: {
                        type: 'shadow'
                    }
                },
    legend: {
        show: false
    },
    grid: {
        left: '50',
        right:'3%',
        top: '6%',
        bottom:0,
        containLabel:true
    },
    xAxis: {
        show: false,
        type: 'value'

    },
    yAxis: [
        {
            type: 'category',
            inverse: true,
            axisLine: {
                show: false
            },
            axisTick: {
                show: false
            },
            axisPointer: {
                label: {
                    show: true,
                    margin: 5
                }
            },
            data:chartData.map(item => item.name),
            axisLabel: {
                show: false,
                inside: false,
                fontSize: 14,
                color: '#88caef',
            },
        }, 
        {
            type: 'category',
            inverse: true,
            axisTick: 'none',
            axisLine: 'none',
            show: true,
            data: chartData.map(item => item.value),
            axisLabel: {
                show:true,
                fontSize: 14,
                color: '#88caef',
                formatter:'{value}'
            }
        }
    ],
    series: [
        {
            z: 2,
            name: 'value',
            type: 'bar',
            barWidth: 5,
            zlevel: 1,
            data: chartData.map((item, i) => {
                let itemStyle = {
                    color: i > 3 ? colorList[0][3] : colorList[0][i]
                }
                return {
                    value: item.value,
                    itemStyle: itemStyle
                };
            }),
            label: {
                normal: {
                    color: '#b3ccf8',
                    show: true,
                    position: [-30, '-15'],
                    textStyle: {
                        fontSize: 12,
                        color: '#88caef',
                    },
                    rich: {
                    a1: {
                        color: '#fff',
                        fontSize: 16,
                        fontWeight:600,
                        backgroundColor: {
                            image: icon1
                        },
                        width: 20,
                        height: 10,
                        align: 'center',
                        borderRadius: 2
                    },
                    a2: {
                        color: '#fff',
                        fontSize: 16,
                        fontWeight:600,
                        backgroundColor: {
                            image: icon1
                        },
                        width: 20,
                        height: 10,
                        align: 'center',
                        borderRadius: 2
                    },
                    a3: {
                        color: '#fff',
                        fontSize: 16,
                        fontWeight:600,
                        backgroundColor: {
                            image: icon1
                        },
                        width: 20,
                        height: 10,
                        align: 'center',
                        borderRadius: 2
                    },
                    b: {
                        color: '#fff',
                        fontSize: 16,
                        fontWeight:600,
                        backgroundColor: {
                            image:icon2
                        },
                        width: 20,
                        height: 10,
                        align: 'center',
                        borderRadius: 2
                    }
                },
                    formatter: (a,b)=> {
                        var index = chartData.map(item => item.name).indexOf(a.name) ;
                        index = index + 1;
                        if (index - 1 < 3) {
                            return [
                                '{a' + index + '|' + index + '}' + '  ' + a.name
                            ].join('\n')
                        } else {
                            return [
                                '{b|' + index + '}' + '  ' + a.name
                            ].join('\n')
                        }
                    }
                }
            },
        },
        {
            name: '总量',
            type: 'bar',
            barWidth: 5,
            barGap: '-100%',
            itemStyle: {
                normal: {
                    color: 'rgba(32, 48, 85, 0.55)'
                }
            },
            data: (new Array(chartData.length)).fill(chartData[0].value+50)
        }

    ]
};
