var bgImg = 'data:image/png;base64,';

var fillImg = 'data:image/png;base64,';
var chartData = [
    {
        name: '2018',
        value: 16589 ,
    }, 
    {
        name: '2017',
        value: 6398 ,
    }, 
    {
        name: '2016',
        value: 3365 , 
    }
];

var bgData = [];
var itemData = [];

// 取出每一条数据value,作为显示数据
chartData.forEach(function(items, index) {
    itemData.push(items.value);
})

// 取出所有数据最大值,作为背景象形柱图数据
chartData.forEach(function(items, index) {
    bgData.push({
        name: items.name,
        value: Math.max.apply(null, itemData)
    });
})

// 所有数据最大值
var maxValue = Math.max.apply(null, itemData);

// 字体 distance放大参数
var scale = 1;

//富文本配置
var rich = {
    white: {
        color: "#fff",
        align: 'left',
        fontSize: 18 * scale,
        padding: [0, 0]
    },
};

var option = {
    // backgroundColor : '#4198f9' ,
    tooltip:{
    	show : true ,
    },
    grid: {
        left: '1%',
        top: '2%',
        containLabel: true
    },
    xAxis: {
        type: 'category',
        data:(function(data){
            var arr=[];
            data.forEach(function(items){
                arr.push(items.name);   
            });
            return arr;
        })(chartData),
        
        
        
        boundaryGap: ['20%', '20%'],
        splitLine: {
            show: false
        },
        axisLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            textStyle: {
                fontSize: 20 * scale,
                color: '#404040'
            }
        }
    },
    yAxis: {
        type: 'value',
        show : false ,
       
    },
    series: [
        //背景
        {
            name: 'bg',
            type: 'pictorialBar',
            barWidth: '45%',
            silent: true,
            symbol: 'image://'+ bgImg,
            symbolClip:false,
            symbolBoundingData: maxValue,
            symbolSize: [30, '100%'],
            data: bgData
        },
        {
            name: '数据',
            type: 'pictorialBar',
            barWidth: '45%',
            barGap: '-100%',
            data: chartData,
            z: 3,
            symbol: 'image://'+ fillImg,
            symbolClip:true,
            symbolBoundingData: maxValue,
            symbolSize: [30, '100%'] ,
			tooltip : {
				formatter : function(datas){
					return datas.data.value + '<br />' ;
				}
			}
        },
    ]
};
