    let item = {
        value: `${v.name}**${v.text}`,
        textStyle: {
            rich: {
                a: {
                    color: "#fff",
                    width: 64,
                    height: 17,
                    align: 'center',
                    backgroundStyle: {
                        color: new echarts.graphic.LinearGradient(0, 0, 1, 1, [{
                        offset: 0,
                        color: '#3D9FFF'
                    }, {
                        offset: 1,
                        color: '#41D7F3'
                    }], false),
                    symbol: disk
