                    verticalAlign: 'middle',
                    lineHeight: 30
                },
                b: {
                },
                value: {
                    align: 'center',
                    fontSize: 16,
                    color: 'rgba(2,247,253,1)'
                }
            }
        }
    }
    return item;
})
