var uploadedDataURL2 = 'data:image/png;base64,';
var indicator = [
    { name: '审计监察部', max: 100, num: 1 },
    { name: '行政事务部', max: 100, num: 2 },
    { name: '信息导航管理', max: 100, num: 3 },
    { name: '安全质量部', max: 100, num: 4 },
    { name: '安检护卫部', max: 100, num: 5 },
];
var dataValue = [43, 90, 80, 53, 78, 89, 77, 50]
var dataArr = [
    {
        value: dataValue,
        name: '',
        itemStyle: {
            normal: {
                lineStyle: {
                    color: '#17E7FF',
                },
                shadowColor: '#17E7FF',
                shadowBlur: 5,
            },
        },
    },
];
function contains(arr, obj) {
  var i = arr.length;
  while (i--) {
    if (arr[i].name === obj) {
      return i;
    }
  }
  return false;
}
var num = 80;
option = {
    backgroundColor: '#000928',
    color: ['#17E7FF'],
    title: {
        text: [`{span|${num}}\n{b|综合评分}`].join(''),
        bottom: 'center',
        left: 'center',
        // triggerEvent: true, //开启雷达图的边角名称可点击
        textStyle: {
            rich: {
                span: {
                    fontSize: 50,
                    fontWeight: 500,
                    color: '#fff',
                    textShadowColor: 'rgba(120, 246, 255, 0.73)',
                    textShadowBlur: -1,
                    textShadowOffsetX: 0,
                    textShadowOffsetY: 2,
                },
                b: {
                    fontSize: 25,
                    color: '#D7F6F3',
                    lineHeight: 40,
                },
            },
        },
