var symbolUrl = 'data:image/png;base64,';
var presents = ['有更多经济需求',
          "娱乐活动简单",
           
          "社交空间单一",
          "集市设施简陋",
          "游客体验感不高",
          "日常较单一",
          "集市停车不便",
          "菜市场利用不高",
          "主街界面封闭",
          "公共空间基本没有",
          '空间利用率低',
          "村镇缺乏活力",
          "缺乏儿童游乐空间",
          "建筑风格少特点",
          "认知度不高",
          "优质的自然人文资源没发挥最好作用",
         
         ]

var data = [];
for (var i = 0; i < presents.length; ++i) {
    data.push({
        name: presents[i],
        value: (presents.length - i) * 20
    });
}
for (var i = 10; i < presents.length; ++i) {
    var cnt = Math.floor(Math.random() * 10);
    for (var j = 0; j < cnt; ++j) {
        data.push({
            name: presents[i],
            value: Math.random() * 10
        });
    }
}

var maskImage = new Image();
maskImage.onload = function() {
    myChart.setOption(
        {
        tooltip: {
            show: false
        },
        backgroundColor:'#00023f',
        grid: {
            left: 0,
            bottom: 0,
            top: 0,
            right: 0,
        },
        xAxis: {
            type: "category",
            show: false
        },
        yAxis: {
            max: 100,
            show: false
        },
        series: [
            {
                type: 'wordCloud',
                sizeRange: [8, 60],
                rotationRange: [0, 0],
                maskImage: maskImage,
                textPadding: 100,
                gridSize: 8,
                width: '100%',
                height: '65%',
                left: 'center',
                top: 'center',
                drawOutOfBound: false,
                textStyle: {
                    normal: {
                        color: function() {
                            return 'rgb(' + [
                                Math.round(Math.random() * 160),
                                Math.round(Math.random() * 160),
                                Math.round(Math.random() * 160)
                            ].join(',') + ')';
                        }
                    },
                },
                data: data
            },
        ]
    });
};
maskImage.src = symbolUrl;
