data.code:
app.title = '堆叠条形图';

var sexIcon = {
    'male': 'data:image/png;base64,',
    'female': 'data:image/png;base64,'
};

option = {
    tooltip : {
        trigger: 'item',
        axisPointer : {            // 坐标轴指示器，坐标轴触发有效
            type : 'shadow'        // 默认为直线，可选为：'line' | 'shadow'
        },
        backgroundColor: 'rgba(255,255,255,0.7)',
        padding: [5, 10],
        textStyle: {
            color: '#444'
        },
        formatter: '{b}:{c}'
    },
    color: ['#45A1FF', '#F04864'],
    xAxis: [{
        type: 'value',
        show: false
    }],
    yAxis: [{
        type: 'category',
        show: false,
        data: ['性别比例']
    }],
    series: [{
            type: 'bar',
            stack: '性别比例',
            barWidth: 10,
            label: {
                normal: {
                    show: true,
                    position: 'left',
                }
            },
            data: [{
                name: '男',
                value: '155',
                label: {
                    position: 'left',
                    normal: {
                        formatter: [
                            '{Male|   }' + '\n\n' + '男' 
                        ].join('\n'),
                        rich: {
                            Male: {
                                height: 40,
                                align: 'center',
                                backgroundColor: {
                                    image: sexIcon.male
                                }
                            }
                        }
                    }
                }
            }]
        },
        {
            type: 'bar',
            stack: '性别比例',
            barWidth: 10,
            label: {
                normal: {
                    show: true,
                    position: 'right'
                }
            },
            data: [{
                name: '女',
                value: '45',
                label: {
                    normal: {
                        formatter: [
                            '{Female|}' + '\n\n' + '女'
                        ].join('\n'),
                        rich: {
                            Female: {
                                height: 40,
                                align: 'center',
                                backgroundColor: {
                                    image: sexIcon.female
                                }
                            }
                        }
                    }
                }
            }]
        }
    ]
};
