var category = ['违规外联', '拒绝服务', '漏洞利用', '随便吧啥'];
var barData = [~~(Math.random() * 100), ~~(Math.random() * 100), ~~(Math.random() * 100), ~~(Math.random() * 100)];
var lineData = [120, 120, 120, 120]
console.log(barData)
option = {
    backgroundColor: '#000',
    xAxis: {
        show: false
    },
    yAxis: {
        data: category,
        show: false
    },
    series: [{
        name: '',
        type: 'pictorialBar',
        symbol: 'image://data:image/svg+xml;base64,',
        barWidth: 100,
        data: lineData,
        barGap: '-100%',
        symbolOffset: [0, '-50%'],
        barCategoryGap: 0,
         // animationEasing: 'elasticOut',
             animationDelay: function (dataIndex, params) {
            return params.index * 30;
        }
    }, {
        name: '',
        type: 'pictorialBar',
        symbol: 'rect',
        itemStyle: {
            normal: {
                color: '#1F4683'
            }
        },
        barWidth: 10,
        symbolRepeat: true,
        symbolSize: 14,
        data: lineData,
        barGap: '-100%',
        barCategoryGap: 0,
        label: {
            normal: {
                show: true,
                formatter: (series) => {
                    return barData[series.dataIndex]
                },
                position: 'insideTopRight',
                offset: [0, -30],
                color: '#fff',
                fontSize: 20
            }
        },
        z: -100,
            animationEasing: 'elasticOut',
             animationDelay: function (dataIndex, params) {
