            }
        },
        symbolRepeat: true,
        symbolSize: 14,
        data: barData,
        label: {
            normal: {
                show: true,
                formatter: '{b}',
                position: [2, -30],
                color: '#fff',
                fontSize: 20
            }
        },
            animationEasing: 'elasticOut',
             animationDelay: function (dataIndex, params) {
            return params.index * 30 * 1.1;
        }
    }],
    animation: true
