 var treeUrl = "data:image/svg+xml;base64,";


 var dataArr = ['小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜','小鱼干', '猫粮', '老鼠', '胡萝卜',]

 var data = [];
 for (var i = 0; i < dataArr.length; ++i) {
     data.push({
         name: dataArr[i],
         value: (dataArr.length - i) * 20
     });
 }
 for (var i = 10; i < dataArr.length; ++i) {
     var cnt = Math.floor(Math.random() * 10);
     for (var j = 0; j < cnt; ++j) {
         data.push({
             name: dataArr[i],
             value: Math.random() * 10
         });
     }
 }

 var maskImage = new Image();
 maskImage.onload = function() {
     myChart.setOption({
         tooltip: {
             show: false
         },
         series: [{
             type: 'wordCloud',
             gridSize: 1,
             sizeRange: [12, 35],
             rotationRange: [0, 90],
             rotationStep: 90,
             maskImage: maskImage,
             textStyle: {
                 normal: {
                     color: function(v) {
                         if (v.value > 500) {
                             return '#ff5722';
                         } else if (v.value > 300) {
                             return '#ff9800';
                         } else if (v.value > 9) {
                             return '#ffc107';
                         } else {
                             return '#ffeb3b';
                         }
                     }
                 }
             },
             width: 600,
             height: 500,
             top: 40,
             data: data
         }]
     });
 };
 maskImage.src = treeUrl;
