var data = [78, 60, 60]
var titlename = ['省级', '市级', '县级'];
var valdata = [683, 234, 234]
var myColor = ['#1089E7', '#F57474', '#56D0E3', '#F8B448', '#8B78F6'];
option = {
    backgroundColor: '#0e2147',
    xAxis: {
        show: false
    },
    yAxis: [{
        show: true,
        data: titlename,
        inverse: true,
        axisLine: {
            show: false
        },
        splitLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            textStyle: {
                color: '#ffffff'
            }
        },

    }, {
        show: true,
        inverse: true,
        data: valdata,
        axisLabel: {
            textStyle: {
                color: function(value, index) {
                    var num = myColor.length;
                    return myColor[index % num]
                }
            },
             formatter: function(value, index) {
                return [
                    '占比：' + value + '% '
                ].join('\n')
            },
        },
        axisLine: {
            show: false
        },
        splitLine: {
            show: false
        },
        axisTick: {
            show: false
        },

    }],
    series: [{
        name: '条',
        type: 'bar',
        yAxisIndex: 0,
        data: data,
        barWidth: 30,
        itemStyle: {
            normal: {
                barBorderRadius: 30,
                color: function(params) {
                    var num = myColor.length;
                    return myColor[params.dataIndex % num]
                },
            }
        },
        label: {
            normal: {
             show: true,
            position: 'insideBottomRight',
            formatter: '{c}%',
            distance: 0,
            offset: [0, -35],
            color: '#fff',
            fontSize: 16,
            padding: [5, 15, 10, 15],
            backgroundColor: {
              image: 'data:image/png;base64,'
            }
            }
        },
    }]
};
