data.code:
 var bg =
            'image://data:image/png;base64,';
        var bar =
            'image://data:image/png;base64,';
        var data = [20, 82, 121, 138, 140, 155, 178, 189, 202, 210];
        var barWidth = 20;
        var maxNum = 0;
        for (var i = 0; i < data.length; i++) {
            if (data[i] > maxNum) {
                maxNum = data[i];
            }
        }

        option = {
            backgroundColor:"black",
            title: {
                text: '不良原因top10',
                textStyle: {
                    align: 'center',
                    color: '#fff',
                    fontSize: 28,
                },
                top: '5%',
                left: 'center',
            },
            grid: {
                top: '15%',
                left: '10%',
                right: '0%',
                bottom: '0%',
            },
            xAxis: {
                show: false,
            },
            yAxis: {
                inverse: true,
                data: data.map((item, index) => `原因 ${index}`),
                splitLine: {
                    show: false,
                },
                axisLabel: {
                    textStyle: { fontSize: '20', color: '#fff' },
                },
                axisLine: {
                    show: false,
                },
                axisTick: false,
            },
            series: [
            {
                type: 'pictorialBar',
                barWidth: barWidth,
                zlevel: 2,
                data: data.map(function (item) {
                    return {
                        value: item,
                        symbol: bar,
                    };
                }),

            },
            {
                type: 'pictorialBar',
                barWidth: barWidth,
                data: data.map(function (item) {
                    return {
                        realValue: item,
                        value: maxNum,
                        symbol: bg,
                    };
                }),
                label: {
                    show: true,
                    position: 'right',
                    distance: 100,
                    align: "right",
                    formatter: function (params) {
                        return params.data.realValue + ' 件';
                    },
                    color: '#fff',
                    fontSize: 22,
                },
            }, ],
        };
