var pic = 'data:image/png;base64,'
var room = 'data:image/png;base64,'
var data = [{
    name: '干部宿舍楼',
    total: 120,
    use: 60,
    empty: 60,
}, {
    name: '公寓住房',
    total: 110,
    use: 40,
    empty: 70,
}]



// 计算总数
var total = 0;
var seriesOption = []
data.map((item, index) => {
    total+=item.total
    seriesOption.push({
        name: '柱背景',
        type: 'bar',
        xAxisIndex: index,
        yAxisIndex: index,
        data: [{

        }, {
            name: item.name,
            value: item.total,
            rate: (item.use / item.total).toFixed(2) * 100 + '%',
        }],
        barWidth: 16,
        itemStyle: {
            normal: {
                barBorderRadius: 8,
                color: '#DFEEFF'
            }
        },
        label: {
            show: true,
            position: 'insideRight',
            formatter: params => {
                return `{pb|使用率：${params.data.rate}}`
            },
            rich: {
                pb: {
                    color: '#4B96F3',
                    padding: [0, 0, -30, 0]
                }
            }
        }
    })
    seriesOption.push({
        value: item.use,
        max: item.total,
        name: item.name,
        zlevel: 2,
        type: 'bar',
        yAxisIndex: index,
        xAxisIndex: index,
        data: [{

        }, {
            value: item.use,
            use:item.use,
            empty:item.empty,
            total:item.total
        }],
        label:{
            show:true,
            color:'transparent',
            position:'insideLeft',
            formatter:params=>{
                console.log(params)
                return `{pb|}\n{imgIcon|}  {normalText|房间：${params.data.total}间}
                \n\n{circleYellow|●}  {normalText|使用：${params.data.use}间}
                \n\n{circleGreen|●}  {normalText|空闲：${params.data.empty}间}`
            },
            rich:{
                circleYellow:{
                    color:'#FFA83E',
                    width:16,
                    height:16,
                    fontSize:20,
                    lineHeight:20,
                    padding:[0,0,0,4]
                },
                pb:{
                    padding:[0,0,200,0]  
                },
                circleGreen:{
                    color:'#36C78B',
                    fontSize:20,
                    width:16,
                    height:16,
                    lineHeight:20,
                    padding:[0,0,0,4]
                },
                imgIcon:{
                    backgroundColor:{
                        image:room,
                    },
                    width:20,
                    height:20,
                },
                normalText:{
                    fontSize:15,
                    height:20,
                    textVeticalAlign:'middle',
                    color:'#666666'
                }
            }
        },
        rate: item.total === 0 ? 0 : (item.use / item.total).toFixed() * 100 + '%',
        barGap: '-100%',
        barWidth: 16,
        itemStyle: {
            normal: {
                barBorderRadius: 8,
                color: '#4B96F3'
            }
        }

    })
})
