data.code:
// 内圈
var img = `data:image/png;base64,`;
var trafficWay = [{
        name: "生产",
        value: 123,
    },
    {
        name: "流通",
        value: 234,
    },
    {
        name: "餐饮",
        value: 345,
    },
];

var data = [];
var color = ["#11D9A1", "#FDBE00", "#0291FD"];
for (var i = 0; i < trafficWay.length; i++) {
    data.push({
        value: trafficWay[i].value,
        name: trafficWay[i].name,
        itemStyle: {
            normal: {
                borderWidth: 5,
                shadowBlur: 0,
                borderColor: color[i],
                shadowColor: color[i],
            },
        },
    }, {
        //间隙
        value: 0,
        name: "",
        itemStyle: {
            normal: {
                label: {
                    show: false,
                },
                labelLine: {
                    show: false,
                },
                color: "rgba(0, 0, 0, 0)",
                borderColor: "rgba(0, 0, 0, 0)",
                borderWidth: 0,
            },
        },
    });
}
var seriesOption = [{
    name: "",
    type: "pie",
    roseType: "radius",
    clockWise: true,
    // radius: [36, 39],
    // center: ["100", "50%"],
    center: ["50%", "50%"],
    radius: ["45%", "75%"],
    hoverAnimation: true,
    itemStyle: {
        normal: {
            label: {
                show: false,
            },
            labelLine: {
                length: 0,
                length2: 1,
                show: false,
                color: "#00ffff",
            },
        },
    },
    data: data,
}, ];
var option = {
    backgroundColor: "",
    color: color,
    graphic: {
        elements: [{
            type: "image",
            z: 3,
            style: {
                image: img,
                width: 350,
                height: 350,
            },
            // left: "70",
            left: "385",
            top: "center",
            position: [100, 100],
        }, ],
    },
    tooltip: {
        confine: true,
        show: false,
    },
    legend: {
        icon: "circle",
        orient: "vertical",
        data: [{
                name: "生产",
                // 强制设置图形为圆。
                icon: "image://data:image/png;base64,",
                // 设置文本为红色
                textStyle: {
                    color: color[0],
                },
            },
            {
                name: "流通",
                // 强制设置图形为圆。
                icon: "image://data:image/png;base64,", // 设置文本为红色
                textStyle: {
                    color: color[1],
                },
            },
            {
                name: "餐饮",
                // 强制设置图形为圆。
                icon: "image://data:image/png;base64,", // 设置文本为红色
                textStyle: {
                    color: color[2],
                },
            },
        ],
        itemWidth: "20",
        itemHeight: "21",
        x: "right", //可设定图例在左、右、居中
        y: "center", //可设定图例在上、下、居中
        symbolKeepAspect: true,
        align: "right",
        itemGap: 10,
        formatter: function(name) {
            var target = 0;
            if (name == trafficWay[0].name) {
                target = trafficWay[0].value;
            } else if (name == trafficWay[1].name) {
                target = trafficWay[1].value;
            } else if (name == trafficWay[2].name) {
                target = trafficWay[2].value;
            }
            var arr = [name + " \n" + target];
            return arr.join("\n");
        },
        // textStyle: {
        //   rich: {
        //     a: {
        //       width: 90,
        //     },
        //   },
        // },
    },
    toolbox: {
        show: false,
    },
    series: seriesOption,
};
