data.code:
var bg =
    'image://data:image/png;base64,';
var bar =
    'image://data:image/png;base64,';
var data = [220, 22, 191, 234, 290, 120, 65, 444];
var barWidth = 20;
var maxNum = 0;
for (var i = 0; i < data.length; i++) {
    if (data[i] > maxNum) {
        maxNum = data[i];
    }
}

option = {
    backgroundColor: 'block',
    grid: {
        top: '10%',
        left: '20%',
        right: '10%',
        bottom: '0%',
    },

    title: {
        text: '各地市人口分布',
        textStyle: { fontSize: '16px', color: '#fff' },
    },
    xAxis: {
        show: false,
    },
    yAxis: {
        data: data.map((item, index) => `data ${index}`),
        splitLine: {
            show: false,
        },
        axisLabel: {
            textStyle: { fontSize: '16px', color: '#fff' },
        },
        axisLine: {
            show: false,
        },
        axisTick: false,
    },
    series: [
        {
            type: 'pictorialBar',
            barWidth: barWidth,
            zlevel: 2,
           data: data.map(function (item,index) {
  				let icon;
  				if(index>3) {
  					icon = bg;
  				} else {
  					icon = bar;
  				}
                  return {
                      value: item,
                      symbol: icon,
                  };
              }),
        },
        {
            type: 'pictorialBar',
            barWidth: barWidth,
            data: data.map(function (item) {
                return {
                    realValue: item,
                    value: maxNum +100,
                    symbol: bg,
                };
            }),
            label: {
                show: true,
                position: 'right', 
                        distance: 80, 
                        align:"right", 
                formatter: function (params) {
                    return params.data.realValue + ' 件';
                },
                color: '#b9a769',
                fontSize: 18,
            },
        },
    ],
};
