data.code:
var fontColor = "#000";
     var seriesName = "";
     let noramlSize = 16;
    var datas = {
       imageUrl:
         "data:image/png;base64,",
       colors: ["#85cbcf", "#3b96f2"],
       legendArr: ["A类", "B类"],
       dataArr: [
         { value: 335, name: "A类" },
         { value: 310, name: "B类" }
       ],
       company: "个"
     };
    option={
        backgroundColor:"#fff",
       color: datas.colors,
       grid: {
         left: "7%",
         right: "7%",
         bottom: "15%",
         top: "5%"
       },
       graphic: {
         elements: [
           {
             type: "image",
             style: {
               image: datas.imageUrl,
               width: 71,
               height: 46
             },
             left: "center",
             top: "40%"
           }
         ]
       },
       tooltip: {
         trigger: "item",
         backgroundColor: "#f6f6f6",
         textStyle: {
           color: "#000"
         },
         formatter: "{b}: {c}"+datas.company+" ({d}%)"
       },
       legend: {
         icon: "rect",
         bottom: "5%",
         left: "center",
         itemWidth: 12,
         itemHeight: 12,
         itemGap: 50, //图例间隙
         textStyle: {
           color: "#000",
           fontSize: 14
         },
         formatter: function(legendData) {
           let names = "";
           let values = "";
           datas.dataArr.forEach(function(ele) {
             if (ele.name == legendData) {
               names = ele.name;
               values = ele.value;
             }
           });
           return names + "   " + values + datas.company;
         },
         data: datas.legendArr
       },
       series: [
         {
           name: "",
           type: "pie",
           radius: ["45%", "60%"],
           center: ["50%", "45%"],
           itemStyle: {
             normal: {
               borderWidth: 3,
               borderColor: "#fff"
             }
           },
           avoidLabelOverlap: false,
           label: {
             normal: {
               show: false,
               position: "center"
             },
             emphasis: {
               show: false,
               textStyle: {
                 fontSize: "30",
                 fontWeight: "bold"
               }
             }
           },
           labelLine: {
             normal: {
               show: false
             }
           },
           data: datas.dataArr
         }
       ]
     };
