data.code:
var fontColor = "#000";
     var seriesName = "";
     let noramlSize = 16;
    var datas = {
       imageUrl:
         "data:image/png;base64,",
       colors: ["#138af4", "#24f0ae", "#f1af19", "#e54835"],
       legendArr: ["A类", "B类", "C类", "D类"],
       dataArr: [
         { value: 335, name: "A类" },
         { value: 210, name: "B类" },
         { value: 110, name: "C类" },
         { value: 410, name: "D类" },
       ],
       company: "个"
     };
    option={
       backgroundColor:"rgb(19, 52, 116)",
       color: datas.colors,
       grid: {
         left: "7%",
         right: "7%",
         bottom: "15%",
         top: "5%"
       },
       graphic: {
         elements: [
           {
             type: "image",
             style: {
               image: datas.imageUrl,
               width: 40,
               height: 36
             },
             left: "center",
             top: "43%"
           }
         ]
       },
       tooltip: {
         trigger: "item",
         backgroundColor: "#f6f6f6",
         textStyle: {
           color: "#000"
         },
         formatter: "{b}: {c}"+datas.company+" ({d}%)"
       },
       legend: {
         icon: "rect",
         bottom: "5%",
         left: "center",
         itemWidth: 12,
         itemHeight: 12,
         itemGap: 50, //图例间隙
         textStyle: {
           color: "#fff",
           fontSize: noramlSize
         },
         width:200,
         data: datas.legendArr
       },
       series: [
         {
           name: "",
           type: "pie",
           radius: ["45%", "60%"],
           center: ["50%", "45%"],
           itemStyle: {
             normal: {
               borderWidth:10,
               borderColor: "rgb(19, 52, 116)"
             }
           },
           avoidLabelOverlap: false,
           label: {
             normal: {
               show: true,
               formatter:function(v){
                 console.log('vvv',v)
                 return `{label|${v.name}}  {value|${v.percent}%}`
               },
               rich:{
                 label:{
                   color:"#94c8fe",
                   fontSize:noramlSize
                 },
                 value:{
                   color:"#fff",
                   fontSize:18
                 }
               }
             },
             emphasis: {
               show: true,
             }
           },
           labelLine: {
             normal: {
               show: true,
               lineStyle:{
                 color:"#138af4"              
               },
               length:20,
               length2:50,
             }
           },
           data: datas.dataArr
         },
         {
           type:"pie",
           radius: "40%",
           center: ["50%", "45%"],
           z:-2,
            label:{
             show:false
           },
           labelLine:{
             show:false
           },
           itemStyle:{
             color:'rgba(255,255,255,0.2)'
           },
            data: [100]
         },
         {
           type:"pie",
           radius: "30%",
           center: ["50%", "45%"],
           z:-1,
           label:{
             show:false
           },
           labelLine:{
             show:false
           },
           itemStyle:{
             color:'rgba(255,255,255,0.2)'
           },
            data: [100]
         }
       ]
     };
