data.code:
var bgImg =
    "data:image/png;base64,"
var fillImg =
    "data:image/png;base64,"
var barname = ['四月新番', '七月新番', '十月新番', '早上吃什么', '中午吃什么', '晚上吃什么', ]
var data1 = [10, 20, 30, 40, 50, 60];
var data1Max = 100;
var data2 = [];
var bgdata = [];
var itemData = [];
// 所有数据最大值
var maxValue = Math.max.apply(null, itemData);
for (var i = 0; i < data1.length; i++) {
    bgdata[i] = data1Max;
    if (data1Max === 0) {
        data2.push(80);
    } else {
        data2.push(data1Max * 1);
    }
}
option = {
    backgroundColor: "#333",
    grid: {
        left: "38%",
        top: "5%",
        bottom: "3%",
        right: "25%",
        // containLabel: true,
        height: "90%",
        // width: "60%"
    },
    legend: {
        show: false
    },
    tooltip: {
        show: true,
        trigger: "axis",
        formatter: "{b}",
        axisPointer: {
            type: 'none'
        },
        confine: true,
    },
    xAxis: {
        splitLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLine: {
            show: false
        },
        axisLabel: {
            show: false
        }
    },
    yAxis: {
        splitLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLine: {
            show: false
        },
        axisLabel: {
            color: "#bee3fd",
            fontSize: 22,
            margin: 120,
            interval: 0,
            align: 'left',
            formatter: (c) => {
                if (c.length > 6) {
                    return c.replace(/.{6}(?!$)/g, (a) => a + '\n')
                } else {
                    return c;
                }
            }
        },
        data: barname
    },
    series: [
        //背景
        {
            name: "bg",
            type: "pictorialBar",
            barWidth: "30",
            silent: true,
            barCategoryGap: 200,
            symbol: "image://" + bgImg,
            symbolClip: false,
            symbolBoundingData: 100,
            symbolSize: ["100%", "100%"],
            data: bgdata
        },
        {
            name: "数据",
            type: "pictorialBar",
            barWidth: "30",
            barGap: "-100%",
            data: data1,
            z: 3,
            symbol: "image://" + fillImg,
            symbolClip: true,
            barCategoryGap: 1000,
            symbolBoundingData: 100,
            symbolSize: ["100%", "100%"],
            label: {
                normal: {
                    show: true,
                    position: "right",
                    fontSize: 24,
                    color: "#fcfbbe",
                    offset: [10, 0],
                    formatter: function(params) {
                        return data1[params.dataIndex] === 0 ?
                            "0" :
                            data1[params.dataIndex] + "%";
                    }
                }
            },
        }
    ]
};
