data.code:
// var bgImg = 'data:image/png;base64,';

// var fillImg = 'data:image/png;base64,';
var chartData = [
    {
        name: '通信',
        value: 2691
    }, 
    {
        name: '网络',
        value: 5000
    }, 
    {
        name: '能源',
        value: 3416
    },
    {
        name: '建筑',
        value: 4666
    },
];

var bgData = [];
var itemData = [];

// 取出每一条数据value,作为显示数据
chartData.forEach(function(items, index) {
    itemData.push(items.value);
})

// 取出所有数据最大值,作为背景象形柱图数据
chartData.forEach(function(items, index) {
    bgData.push({
        name: items.name,
        value: Math.max.apply(null, itemData)
    });
})

// 所有数据最大值
var maxValue = Math.max.apply(null, itemData);

// 字体 distance放大参数
var scale = 1;

//富文本配置
var rich = {
    white: {
        color: "#fff",
        align: 'left',
        fontSize: 18 * scale,
        padding: [0, 0]
    },
};

var option = {
    backgroundColor: '#031f2d',
    tooltip:{
        formatter:'{b} : {c}',
    },
    grid: {
        left: '3%',
        right: '6%',
        bottom: '3%',
        containLabel: true
    },
    xAxis: [{
        type: 'category',
        data:(function(data){
            var arr=[];
            data.forEach(function(items){
                arr.push(items.name);   
            });
            return arr;
        })(chartData),
        
        boundaryGap: ['20%', '20%'],
        splitLine: {
            show: false
        },
        axisLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            margin:20,
            textStyle: {
                fontSize: 20 * scale,
                color: '#fff'
            }
        }
    }],
    yAxis: [{
        type: 'value',
        splitLine: {//横向网格线
            show: true,
            lineStyle: {
                color: "rgb(35, 39, 50,0.1)",
                width:2,
                type: 'solid'
            }
        },
        axisLine: {
            show: false
        },
        axisTick: {
            show: false,
            
        },
        axisLabel: {
            textStyle: {
                color: '#fff',
                fontSize: 18 * scale
            }
        },
         
    }],
    series: [
        //背景
        {
            name: 'bg',
            type: 'pictorialBar',
            barWidth: '45%',
            // barGap: '0%',
            silent: true,
            label: {//柱子顶部数据展示
                normal: {
                    show: false,
                    position: "top",
                    distance: 20 * scale,
                    formatter: function(params) {
                        var stuNum = 0;
                        chartData.forEach(function(value, index, array) {
                            if (params.name == value.name) {
                                stuNum = value.value;
                            }
                        })
                        return stuNum + '{white| 人}';
                    },
                    textStyle: {
                        color: "#ffc72b",
                        fontSize: 20 * scale
                    },
                    rich: rich
                }
            },
             itemStyle: {
                normal: {
                    barBorderRadius: 5,
                    color: 'rgb(45, 46, 48)',

                }
            },
            // symbol: 'image://'+ bgImg,//可以自定义形状
             symbol: 'rect',
             symbolRepeat: 'fixed',
            symbolMargin: '5%',
            symbolSize: [40,40],
            symbolClip:false,
            symbolBoundingData: maxValue,
            animationEasing: 'elasticOut',//渲染时的动画效果
            // symbolSize: [52, '100%'],
            data: bgData
        },
        {
            name: '数据',
            type: 'pictorialBar',
            barWidth: '45%',
            barGap: '-100%',
            data: chartData,
            z: 3,
             itemStyle: {
                normal: {
                    barBorderRadius: 5,
                    color:  'rgb(52, 151, 252)'//渐变色需要通过图片 symbol: 'image://'+ fillImg,

                }
            },
            // symbol: 'image://'+ fillImg,
              symbol: 'rect',
               symbolRepeat: 'fixed',
            symbolMargin: '5%',
            symbolSize: [40,40],
            symbolClip:true,
            symbolBoundingData: maxValue,
            // symbolSize: [52, '100%']
        },
    ]
};
