let img1 = 'data:image/png;base64,'
let img2 = 'data:image/png;base64,'
let img3 = 'data:image/png;base64,'
let barData = [{
        name: '物资1',
        num: 20
    },
    {
        name: '物资1',
        num: 50
    },
    {
        name: '物资1',
        num: 80
    },
    {
        name: '物资1',
        num: 100
    },
    {
        name: '物资1',
        num: 60
    },
    {
        name: '物资1',
        num: 10
    },
    {
        name: '物资1',
        num: 44
    },
    {
        name: '物资1',
        num: 16
    },
    {
        name: '物资1',
        num: 82
    }
]
let bar = (data, dom) => {
    let color1 = {
        type: 'linear',
        colorStops: [{
                offset: 0,
                color: 'rgb(27,124,177)'
            },
            {
                offset: 0.2,
                color: 'rgb(23,196,230)'
            },
            {
                offset: 0.5,
                color: 'rgb(6,120,221)'
            },
            {
                offset: 0.8,
                color: 'rgb(27,196,227)'
            },
            {
                offset: 1,
                color: 'rgb(43,113,164)'
            }
        ]
    }
    let color2 = {
        type: 'radial',
        colorStops: [{
                offset: 0,
                color: 'rgba(13,47,85,.8)'
            }, {
                offset: 0.5,
                color: 'rgba(33,74,118,.8)'
            },
            {
                offset: 1,
                color: 'rgba(33,74,118,.8)'
            }
        ]
    }
    let color3 = {
        type: 'radial',
        colorStops: [{
                offset: 0,
                color: 'rgba(17,47,75,.7)'
            },
            {
                offset: 1,
                color: 'rgba(51,205,207,.7)'
            }
        ]
    }
    let xLabelData = []
    let bgData = []
    let innerData = []
    data.forEach(item => {
        xLabelData.push(item.name)
        innerData.push(item.num)
        bgData.push(100)
    })
    let innerTopData = (_data) => {
        let arr = []
        let height = (dom.clientHeight - 126) / 100
        _data.forEach(item => {
            if (item * height > 20) {
                arr.push({
                    value: item
                })
            } else {
                arr.push({
                    value: item,
                    symbolOffset: [0, -25 + item * height]
                })
            }
        })
        return arr
    }
    let innerShadowData = (_data) => {
        let arr = []
        let height = (dom.clientHeight - 126) / 100
        _data.forEach(item => {
            if (item < 100) {
                if (item * height > 20) {
                    arr.push({
                        value: item
                    })
                } else {
                    arr.push({
                        value: item,
                        symbolOffset: [0, -47.5 + item * height]
                    })
                }
            } else {
                arr.push({
                    value: ''
                })
            }
        })
        return arr
    }
    let option = {
        // animation: false,
        backgroundColor: '#000',
        grid: {
            top: 40,
            left: 25,
            bottom: 30,
            right: 25,
            containLabel: true
        },
        xAxis: {
            data: xLabelData,
            axisTick: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: 'rgb(2,185,208)'
                }
            },
            axisLabel: {
                textStyle: {
                    color: 'rgb(2,219,227)',
                    fontSize: 16,
                    fontWeight: 'bold'
                },
                margin: 20
            }
        },
        yAxis: {
            axisTick: {
                show: true
            },
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: 'rgb(2,185,208)'
                }
            },
            axisLabel: {
                textStyle: {
                    color: 'rgb(2,185,208)'
                },
                formatter: (value, index) => {
                    if (value === 0 || value === 50 || value === 100) {
                        return value + '%'
                    } else {
                        return ''
                    }
                }
            },
            splitNumber: 10
        },
        series: [{ //空柱子label
            type: 'bar',
            itemStyle: {
                normal: {
                    color: 'transparent'
                }
            },
            label: {
                show: true,
                position: 'insideTop',
                distance: -30,
                rich: {
                    a: {
                        color: 'rgb(2,219,227)',
                        fontSize: 16,
                        align: 'left',
                        padding: [0, 0, 0, 120],
                        width: 40,
                        height: 16,
                        lineHeight: 16,
                        backgroundColor: {
                            image: img3
                        },
                    }
                },
                formatter: (params) => {
                    return '{a|' + params.value + '%}'
                }
            },
            z: 5,
            data: innerData
        }, { //蓝色柱子
            type: 'pictorialBar',
            itemStyle: {
                normal: {
                    color: color1
                }
            },
            symbol: 'rect',
            symbolClip: true,
            symbolSize: [35, '100%'],
            symbolOffset: [0, -20],
            symbolPosition: 'start',
            z: 5,
            animation: false,
            data: innerData
        }, { //蓝色柱子顶部阴影椭圆
            type: 'pictorialBar',
            itemStyle: {
                normal: {
                    color: color3
                }
            },
            symbolSize: [35, 10],
            symbolOffset: [0, -27.5],
            symbolPosition: 'end',
            z: 2,
            animation: false,
            data: innerShadowData(innerData)
        }, { //蓝色柱子顶部椭圆
            type: 'pictorialBar',
            itemStyle: {
                normal: {
                    color: 'rgb(51,255,207)'
                }
            },
            symbolSize: [35, 10],
            symbolOffset: [0, -5],
            symbolPosition: 'end',
            z: 6,
            animation: false,
            data: innerTopData(innerData)
        }, { //蓝色柱子底部椭圆
            type: 'pictorialBar',
            itemStyle: {
                normal: {
                    color: color1
                }
            },
            symbolSize: [35, 10],
            symbolOffset: [0, -15],
            symbolPosition: 'start',
            z: 5,
            animation: false,
            data: innerData
        }, { //满的背景柱子
            type: 'pictorialBar',
            symbol: 'image://' + img1,
            symbolClip: true,
            // symbolSize: [35, dom.$el.clientHeight - 136],
            symbolSize: [35, '100%'],
            symbolOffset: [0, -20],
            symbolPosition: 'start',
            z: 2,
            animation: false,
            data: bgData
        }, { //满的顶部椭圆
            type: 'pictorialBar',
            itemStyle: {
                normal: {
                    color: color2
                }
            },
            symbolSize: [35, 10],
            symbolOffset: [0, -5],
            symbolPosition: 'end',
            z: 3,
            animation: false,
            data: bgData
        }, { //底部投影
            type: 'pictorialBar',
            symbol: 'image://' + img2,
            symbolSize: [65, 20],
            symbolOffset: [0, -8],
            symbolPosition: 'start',
            z: 2,
            animation: false,
            data: bgData
        }]
    }
    return option
}
option = bar(barData, document.getElementById('chart-panel'))
