data.code:
let imgSrc = 'data:image/png;base64,';
// 数据初始化
let scale = 1,
    bgColor = "rgba(19,22,24,1)",
    valueColor = '#fff',
    echartData = [{
            name: "1分",
            value: 100
        },
        {
            name: "2分",
            value: 200
        },
        {
            name: "3分",
            value: 200
        },
        {
            name: "4分",
            value: 100
        },
        {
            name: "5分",
            value: 100
        }
    ];
// 数据整理
let total = echartData.reduce((a, b) => {
    return a + b.value
}, 0);
option = {
    backgroundColor: bgColor,
    color: ['#06FDBC', '#F6FE05', '#07B0FE', '#FDB408', '#00DB1C', '#0188FE'],
    tooltip: {
        trigger: "item",
        formatter: "{b}: {c} ({d}%)"
    },
    graphic: {
        elements: [{
            type: "image",
            style: {
                image: imgSrc,
                width: 240,
                height: 240
            },
            left: 'center',
            top: "center",
            position: [100, 100]
        }]
    },
    title: {
        text: '{a|' + total + '}\n{b|评分总人数}',
        top: 'center',
        textAlign: "center",
        left: "50%",
        textStyle: {
            color: "#fff",
            rich: {
                a: {
                    fontSize: 36 * scale,
                    lineHeight: 48 * scale,
                    fontWeight: 'bold'
                },
                b: {
                    fontSize: 20 * scale
                }
            }
        }
    },
    legend: {
        top: "center",
        orient: 'vertical',
        left: "75%",
        icon: "rect",
        itemWidth: 2,
        itemHeight: 12,
        itemGap: 13,
        textStyle: {
            rich: {
                name: {
                    color: "#fff",
                    fontSize: 12 * scale,
                    width: 60
                },
                percent: {
                    color: '#18DB9F',
                    fontSize: 18 * scale
                },
                unit: {
                    color: "#5A717A",
                    fontSize: 12 * scale
                }
            }
        },
        formatter: function(name) {
            let res = echartData.filter(v => v.name === name);
            let percent = ((res[0].value * 100) / total).toFixed(2);
            return (
                "{name| " + name + "}{percent| " + percent + "}" + "{unit| %}"
            );
        }
    },
    toolbox: {
        show: false
    },
    series: [{
        name: "",
        type: "pie",
        radius: [120, 135],
        center: ["50%", "50%"],
        label: {
            normal: {
                show: false
            }
        },
        // hoverAnimation: false,
        hoverOffset: 5,
        itemStyle: {
            normal: {
                borderColor: bgColor,
                borderWidth: 1
            }
        },
        data: echartData
    }]
};
