data.code:
var top = "data:image/png;base64,"

var data = [
    {x: "1", value: 120,value1:55},
    {x: "2", value: 120,value1:75},
    {x: "3", value: 120,value1:90},
    {x: "4", value: 120,value1:120},
    {x: "5", value: 90,value1:130},
    {x: "6", value: 50,value1:150},
    {x: "7", value: 50,value1:155}
]
var imgList = []
data.forEach((item, index) => {
    imgList.push({
        coord: [index, item.value1 + item.value],
        symbolSize: [15, 15],
        symbolOffset: [0, 0],
        symbol: "image://" + top,
        value: item.value,
        label: {
            show: false,
            offset: [0, -30],
            fontSize: 16,
            color: "#00baff"
        }
    }, {
        coord: [index, item.value1],
        symbolSize: [15, 15],
        symbolOffset: [0, 0 ],
        symbol: "image://" + top,
        value: item.value,
        label: {
            show: false,
            offset: [0, 0],
            fontSize: 16,
            color: "#00baff"
        }
    })
})

option = {
    tooltip: {
        trigger: 'item'
    },
    xAxis: {
        type: 'category',
        splitLine: {
            show: false
        },
        data: ['热响应试验', '低压放电试验', '热真空第一循环', '热真空第二循环', '整形综合测试1', '整形综合测试2', '整形综合测试3']
    },
    yAxis: {
        type: 'value',
        axisTick: {
            show: false
        },
        splitLine: {
            show: false
        },
        axisLabel : {
            formatter: function (value,index) {
                // value格式化成月/日，只在第一个刻度显示年份
                var date = new Date(2021,04);
                var texts = [];

                if (index === 1) {
                    texts = [(date.getMonth()+1), date.getDate()];
                    texts.unshift(date.getFullYear());
                }
                else {
                    texts = [(date.getFullYear()),(date.getMonth() + parseInt(index)), date.getDate()];
                }
                return texts.join('-');
            }
        },
        data:['2021/3/26',]
    },
    series: [{
            name: '',
            type: 'bar',
            stack: 'A',
            barWidth: 28,
            itemStyle: {
                normal: {
                    barBorderColor: 'rgba(0,0,0,0)',
                    color: 'rgba(0,0,0,0)'
                },
                emphasis: {
                    barBorderColor: 'rgba(0,0,0,0)',
                    color: 'rgba(0,0,0,0)'
                }
            },
            markPoint: {
               data: imgList
            },
            data: [55, 75, 90, 120, 130, 150, 155]
        },
        {
            name: '',
            type: 'bar',
            stack: 'A',
            barWidth: 28,
            label: {
                normal: {
                    show: false,
                    position: 'top'
                }
            },
            itemStyle: {
                normal: {
                    barBorderRadius: 20,
                    color: new echarts.graphic.LinearGradient(1, 0, 0, 0, [{
                        offset: 1,
                        color: '#3bffdf'
                    }, {
                        offset: 0,
                        color: '#15b6d0'
                    }], false),
                },
            },

            data: [120, 120, 120, 120, 90, 50, 50]
        }
    ]
};
