const imgArr = [
	'image://data:image/png;base64,',
	'image://data:image/png;base64,'
]
option={
    backgroundColor:'#fff',
    color:['#F5A623','#5CCCA6'],
	grid: {
		top: '15%',
		right: '7%',
		left: '10%',
		bottom: '12%'
	},
	tooltip: {
		trigger: 'axis',
		axisPointer: {
			type: 'shadow',
			shadowStyle: {
				//color: 'rgba(93,203,166,0.05)'
				color: new echarts.graphic.LinearGradient(1, 1, 0, 0, [{
                        offset: 0,
                        color: 'rgba(93,203,166,0.1)'
                    },
                    {
                        offset: 1,
                        color: 'rgba(93,203,166,0.02)'
                    }
                ]),
			}
		},
		backgroundColor: '#fff',
		textStyle: {
			color: '#5c6c7c'
		},
		padding: [10, 10],
		extraCssText: 'box-shadow: 0 1px 11px 0 rgba(0,30,53,0.14);border-radius: 1.71px;',
		formatter: function(params) {
			//	 console.log(params)


		}
	},
	legend: {
		left:'center',
		bottom: 15,
		icon:"circle",
		textStyle: {
			fontSize: 12,
			color: '#666'
		},
		data: ['预测收入', '售电收入']
	},
	xAxis: [{
		type: 'category',
		color: '#59588D',
		data: ['2016', '2017', '2018', '2019', '2020'],
		axisLabel: {
			color: '#666',
			textStyle: {
				fontSize: 12
			},
		},
		axisLine: {
			lineStyle: {
				color: '#e6e6e6',
			}
		},
		axisTick: {
			show: false
		},
	}],
	yAxis: [{
		axisLabel: {
			color: '#666',
			textStyle: {
				fontSize: 12
			},
		},
		axisLine: {
			show: false,
			lineStyle: {
				color: 'rgba(107,107,107,0.37)',
			}
		},
		axisTick: {
			show: false
		},
		splitLine: {
			lineStyle: {
				color: '#e6e6e6',
				width: 1,
				type: 'dashed',
			}
		}
	}],
	series: [{
			name: "预测收入",
			type: "line",
			smooth: true,
			zlevel: 2,
			showSymbol: false,
			symbolSize: 22,
			symbol: imgArr[0],
			lineStyle: {
				normal: {
					color: '#F5A623',
					shadowBlur: 4,
					shadowColor: 'rgba(245,166,35,0.50)',
					shadowOffsetY: 6,
					borderWidth: 2,
				}
			},
			data: [{
