let chartData = [];
nameList.map((item, idx) => {
    chartData.push({
        name: item + '年',
        value: valueList[idx],
    });
});
let arrName = [];
let arrValue = [];
let sum = 0;
let pieSeries = [],
    lineYAxis = [];

// 数据处理
chartData.forEach((v, i) => {
    arrName.push(v.name);
    arrValue.push(v.value);
    sum = sum + v.value;
});

function getRadius(i) {
    return [67 - i * 12 + '%', 59 - i * 12 + '%'];
}

// 图表option整理
chartData.forEach((v, i) => {
    pieSeries.push({
        name: '生均占地面积（平方米）',
        type: 'pie',
        clockWise: true,
        hoverAnimation: false,
        radius: getRadius(i),
        center: ['50%', '50%'],
        itemStyle: {
            color: colorList[i],
        },
        label: {
            show: false,
        },
        data: [
            {
                value: v.value,
                name: v.name,
            },
            {
                value: sum - v.value,
                name: 'no',
                itemStyle: {
                    color: 'rgba(0,0,0,0)',
                },
            },
        ],
    });
