    //         }
    //     }]
    // });
    v.percent = ((v.value / sum) * 100).toFixed(2);
    lineYAxis.push({
        value: i,
        textStyle: {
            rich: {
                circle: {
                    color: color[i],
                    padding: [0, 5],
