function _ledgend(){
		
	let legendDt =  new Set(demo && demo.map(item=>item.z));
	let _data = [];
	if(legendDt){
		for (let x of legendDt) { // 遍历Set
			_data.push(x)
		}
	}
	return _data;
	
}

function _ledgData(){
		
	let legendDt =  new Set(demo && demo.map(item=>item.z));
	let _lgdata = [],lgDt=[];
	if(legendDt){
		for (let x of legendDt) { // 遍历Set
			_lgdata.push(x)
		}
	}
	//console.log(_lgdata);
	_lgdata && _lgdata.forEach((item)=>{  
		
		lgDt.push({
			name: item,
			icon: 'circle',
			textStyle: {
				color: '#fff',
				fontSize:14
			}
		})
	});

	return lgDt;

}

function _setSeriesArray() {
		
	let _point = _icon(),
		areaSty = _areaStyleColor(),
		colors = _colors();
	let dataKinds = _ledgend(),
		dataLg = demo.length,
		kinds = dataKinds.length;  //数据种类数n
	let _xData = _xAxisData();
	let result = [];  //n个数组
		
	let obj = {};
    demo.forEach((item, i) => {
		!obj[item.z] ? obj[item.z] = [item] : obj[item.z].push(item);
	})	
	
	for(var i=0;i<kinds;i++){
		let _data = obj[dataKinds[i]],
			tempArr = [];
		for(var k=0;k<dataLg;k++){
			let _dt = _xData[k];
			_data.map((item)=>{
				if(item.x === _dt){
					tempArr.push(item.y)
				}
			})				
		}
		result.push(tempArr)
		
	}
	//console.log(result);

	//最大值组合
	let	maxArr = [];
	result && result.map((item)=>{
		let tempArr = item.concat(),
			max =  tempArr.sort(_sortNumber).slice(0,1)+'';  //深拷贝
		maxArr.push(max);
	})
	
	//创建series
	let seriesArr = [];
	let _markPoint = _markPointData();
	result && result.map((item,ind)=>{
		
		seriesArr.push({
			name: dataKinds[ind],
			type:'line',
			data:item,
			symbolSize:0,
			markPoint:{
				symbol: `image://${_point[ind]}`,
				symbolSize:(value,params)=>{
					let val = params.data.coord[1] +'';
					let ratio = (maxArr.indexOf(val) === params.seriesIndex)? 40: 25;
					return ratio
				},
				label:{
					normal:{
						show:false
					}
				},
				data:_markPoint[ind]
			},
			lineStyle:{
				normal:{
					color: colors[ind],
					shadowColor:colors[ind],
					shadowBlur: 20,
					shadowOffsetX: 1,
					shadowOffsetY: 0,
					opacity:1
				}
				
			},
			areaStyle: {
				normal:{						
					color: {
						type: 'linear',
						x: 0,
						y: 0,
						x2: 0,
						y2: 1,
						colorStops: [{
							offset: 0, color:areaSty[ind][0]  // 0% 处的颜色
						},{
							offset: 1, color: areaSty[ind][1] // 100% 处的颜色
						}],
						globalCoord: false // 缺省为 false
					}
				}
			}
		})
	})
	
	//console.log("seriesArr",seriesArr);
	return seriesArr;
	
}
	
//数组排序不去重
function _sortNumber(a,b){
	return b-a;
}

function  _markPointData(){
	let dataKinds = _ledgend(),
		dataLg =demo.length,
		kinds = dataKinds.length,
		_xData =_xAxisData(); //数据种类数n
	let arr = [],obj = {},result=[];
	
    demo.forEach((item, i) => {
		!obj[item.z] ? obj[item.z] = [item] : obj[item.z].push(item);
	})	
	
	for(var i=0;i<kinds;i++){
		let _data = obj[dataKinds[i]],
			tempArr = [];
		for(var k=0;k<dataLg;k++){
			let _dt = _xData[k];
			_data.map((item)=>{
				if(item.x === _dt){
					tempArr.push({
						x:item.x,
						y:item.y
					})
				}
			})				
		}
		result.push(tempArr)
		
	}

	for(var n=0;n<kinds;n++){
		let temp = [];
		result[n] && result[n].map((item)=>{
			
		 	temp.push({
		 		name:'p',
		 		coord:[item.x,item.y]
		 	})
		})
		arr.push(temp);
	}
	
	return arr
	
}
	
function _xAxisData() {
	let xData =  new Set(demo &&demo.map(item=>item.x));
	let _data = [];
	if(xData){
		for (let x of xData) { // 遍历Set
			_data.push(x)
		}
	}
	//console.log(_data);
	return _data;
	
}
	
function _axisLine() {
	return {
		show: false,
		lineStyle: {
			color: 'rgba(255,255,255,0.2)', //坐标轴线颜色
			width: 2
			
  		}
	}
}

function _icon(){
	const orange = 'data:image/png;base64,';
    const blue = 'data:image/png;base64,';
	const orRed = 'data:image/png;base64,';
	const green = 'data:image/png;base64,';
	
	return [orange,blue,orRed,green]
}
function _colors(){
	return ['#f3972f','#327bfa','#f85b2c','#70e8c2']
}
function _areaStyleColor(){
		
	return [ 
		['rgba(243,151,47,0.3)','rgba(243,151,47,0.1)'],
		['rgba(50,123,250,0.3)','rgba(50,123,250,0.1)'],
		['rgba(248,91,44,0.3)','rgba(248,91,44,0.1)'],
		['rgba(112,232,194,0.3)','rgba(112,232,194,0.1)']
	];
}
