var getsjjg=['普通文理生','体艺生','特长生','港澳台特招生','其他'];
var getsjjgrs=[35,30,25,12,8];
var syjgdata = [];
for(var i = 0;i<getsjjg.length;i++){
	syjgdata.push({name:getsjjg[i],value:getsjjgrs[i]})
}

var colorList=['#41EFFF','#2E74FF','#1DF1CC','#9FF668','#F04F8D'];
option = {
    backgroundColor:'#000',
    graphic: {
        elements: [{
            type: "image",
            z: 3,
            style: {
                image: 'data:image/png;base64,',
                width:130,
                height: 130
            },
            left: 'center',
            top: 'center',
        }]
    },
    series: {
            itemStyle: {
                normal: {
                    color: function(params) {
                     return colorList[params.dataIndex]
                    }
                }
            },
            type: 'pie',
            //clockWise: false ,
			roseType: 'radius',
            radius: ['15%', '53%'],
			center: ["center", "center"], 
            labelLine: {
                normal: {
                    length: 15,
                    length2: 120,
                }
            },
            label: {
                normal: {
                    formatter: params => {
                        return (
                            '{radius|●}\n{percent|'+ params.percent.toFixed(0) +'%}\n\n{name|' + params.name + '}'
                        );
                    },
                    padding: [0, -100, 0, -100],
                    rich: {
						radius: {
							//backgroundColor: 'radius',
							//borderRadius: 3,
							//width: 3,
							//height: 3,
							padding: [-37, 90, 0, 90]
						},
                        name: {
                            fontSize: 12,
                            color: '#7594BA',
                        },
                        percent: {
                            fontSize: 14,
                            color: '#06E9F3'
                        },
                    }
                }
            },
            data: syjgdata
        }
};
