data.code:
var salvProName = ["上海", "四川", "重庆", "西安", "云南"];
var dataArray = [22000, 15000, 9000, 8000, 7000];
var maxValue = []; //背景按最大值
var barImage = 'data:image/png;base64,';
var PatternImgA = new Image();
PatternImgA.src = barImage;
for (let i = 0; i < dataArray.length; i++) {
    maxValue.push(dataArray[0])
}
option = {
    backgroundColor: "#fff",
    grid: {
        left: '2%',
        right: '2%',
        bottom: '0%',
        top: '5%',
        containLabel: true
    },
    title: {
        left: 'center',
        text: '城市分布',
        top: 15,
        textStyle: {
            fontSize: '15',
            fontWeight: 500
        }
    },
    tooltip: {
        trigger: 'axis',
        axisPointer: {
            type: 'none'
        },
        formatter: function(params) {
            return params[0].name + ' : ' + params[0].value
        }
    },
    xAxis: {
        show: false,
        type: 'value'
    },
    yAxis: [{
        type: 'category',
        inverse: true,
        axisLabel: {
            show: true,
            textStyle: {
                // color: 'red',
                fontSize: 13,
                fontWeight: 500
            },
        },
        splitLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLine: {
            show: false
        },
        data: salvProName
    }, {
        type: 'category',
        inverse: true,
        axisTick: 'none',
        axisLine: 'none',
        show: true,
        axisLabel: {
            textStyle: {
                color: '#4B4B4B',
                fontSize: '12'
            },
            formatter: function(value) {
                return (value * 10 / 10).toLocaleString() + '人';
            },
        },
        data: dataArray
    }],
    series: [{
            name: '值',
            type: 'bar',
                zlevel:1,
            barCategoryGap: 23,
            itemStyle: {
                normal: {
                    barBorderRadius: 10,
                    color: new echarts.graphic.LinearGradient(0, 0, 1, 0, [{
                        offset: 0,
                        color: 'rgb(253,188,121)'
                    }, {
                        offset: 1,
                        color: 'rgb(249,131,71)'
                    }]),
                },
            },
            barWidth: 16,
            data: dataArray
        },
        {
            //这里设置包含关系，只需要这一句话
            barGap: "-100%",
            type: 'bar',
            zlevel:1,
            data: dataArray,
            barWidth: 16,
            barCategoryGap: 23,
            itemStyle: {
                //此处引入条纹背景
                color: {
                    image: PatternImgA,
                    repeat: 'repeat'
                },
                barBorderRadius: [16, 16, 0, 0],
                xAxisIndex: 2,
                yAxisIndex: 2,
                zlevel: 2
            }
        },
        {
            name: '背景',
            type: 'bar',
            barWidth: 15,
            barGap: '-100%',
            data: maxValue,
            itemStyle: {
                normal: {
                    color: 'rgba(24,31,68,0.1)',
                    barBorderRadius: 30,
                }
            },
        },
    ]
};
