data.code:
var  category= [{
        name: "完成",
        value: 612.5
    }]; // 类别
var total = 1000; // 数据总数
var  datas = [];
category.forEach(value => {
    datas.push(value.value);
});
option = {
    backgroundColor: '#071347',
    xAxis: {
        max: total,
        splitLine: {
            show: false
        },
        axisLine: {
            show: false
        },
        axisLabel: {
            show: false
        },
        axisTick: {
            show: false
        },
    },
    grid: {
        left: '15%',
        right: '10%',
    },
    yAxis: [{
        type: "category",
        inverse: false,
        stack: '1',
        data: category,
        axisLine: {
            show: false
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            show: false
        },


    }],
    series: [{
            // 内
            type: "bar",
                stack: '1',
            barWidth: 38,
            silent: true,
            itemStyle: {
                normal: {
                    color: '#28EAFD'
                }
            },
            label: {
                normal: {
                    show: true,
                    position:'right',
                    textStyle:{
                        fontSize:20,
                        color:'#fff',
                        rich:{
                            a:{
                                backgroundColor:{
                                    image:'data:image/png;base64,',
                                },
                                
                            },
                            b:{
                                fontSize:26,
                                lineHeight:40,
                                
                            }
                        }
                    },
                    formatter:(params) => {
                        return '\n\n\n\n\n\n{a|}\n'+params.name+'  {b|'+ params.value +'}'
                    }
                }
            },
            data: category,
            z: 1,
            animationEasing: "elasticOut"
        },
        {
            // 分隔
            type: "pictorialBar",
            itemStyle: {
                normal: {
                    color: "#0F2B57"
                }
            },
            symbolRepeat: "fixed",
            symbolMargin: 20,
            symbol: "rect",
            symbolClip: true,
            symbolSize: [3, 38],
            symbolPosition: "start",
            symbolOffset: [0, 0],
            symbolBoundingData: this.total,
            data: [total],
            z: 2,
            animationEasing: "elasticOut",


        },
        {
            // label
            type: "pictorialBar",
            symbolBoundingData: total,
            itemStyle: {
                normal: {
                    color: "none"
                }
            },
            data: datas,
            z: 0,

        },
        {
            name: "外框",
            type: "bar",
            barGap: "-100%", 
            data: [total],
            barWidth: 38,
            itemStyle: {
                normal: {
                    color: "#144E76", 
                }
            },
            z: 0
        },
    ]
}
