data.code:
let barWidth = 10 /* 进度条及进度条radius宽度 */,
    nameWidth = 50 /* 进度条名称宽度 */,
    unit = '亿' /* 单位 */,
    reseachData = [] /* 科研费 */,
    orderData = [] /* 订购费 */,
    repairData = [] /* 维修费用 */,
    attaVal = [] /* 进度条数值 */,
    topName = [] /* 进度条名称 */,
    img =
        'data:image/png;base64,',
    img1 =
        'data:image/png;base64,',
    img2 =
        'data:image/png;base64,';
let datas = [
    {
        name: '空军',
        research: 2400,
        order: 200,
        repair: 400,
        total: 3000,
    },
    {
        name: '海军',
        research: 1800,
        order: 200,
        repair: 400,
        total: 2400,
    },
    {
        name: '火箭军',
        research: 2900,
        order: 600,
        repair: 400,
        total: 3900,
    },
    {
        name: '陆军',
        research: 2400,
        order: 900,
        repair: 700,
        total: 4000,
    },
    {
        name: '陆军',
        research: 2000,
        order: 200,
        repair: 400,
        total: 2600,
    },
];
datas.forEach((item, i) => {
    topName[i] = item.name;
    attaVal[i] = item.total;
    reseachData[i] = {
        value: ((item.research / item.total) * 100).toFixed(2),
        name: item.research,
    };
    orderData[i] = {
        value: ((item.order / item.total) * 100).toFixed(2),
        name: item.order,
    };
    repairData[i] = {
        value: ((item.repair / item.total) * 100).toFixed(2),
        name: item.repair,
    };
});
option = {
    backgroundColor:'#A4DAF4',
    tooltip: {
        show: true,
        backgroundColor: 'rgba(3,169,244, 0.5)', //背景颜色（此时为默认色）
        textStyle: {
            fontSize: 16,
        },
        formatter: (val) => {
            return `${val.seriesName}<br />金额：${val.name}(${unit}元)&emsp;占比：${val.value}%;`;
        },
    },
    grid: {
        left: '10%',
        right: '5%',
        top: '5%',
        bottom: '5%',
        containLabel: true,
    },
    legend: {
        show: false,
    },
    xAxis: {
        show: false,
        max: 100,
    },
    yAxis: [
        //名称
        {
            type: 'category',
            inverse: true,
            axisTick: 'none',
            axisLine: 'none',
            show: true,
            axisLabel: {
                textStyle: {
                    color: '#1F4265',
                },
                formatter: (val, i) => {
                    if (i < 3) {
                        return `{img${i}|}{name|${val}}`;
                    }
                    return `{num|${i + 1}}{name|${val}}`;
                },
                rich: {
                    img0: {
                        backgroundColor: {
                            image: img,
                        },
                        width: 30,
                        height: 30,
                    },
                    img1: {
                        backgroundColor: {
                            image: img1,
                        },
                        width: 30,
                        height: 30,
                    },
                    img2: {
                        backgroundColor: {
                            image: img2,
                        },
                        width: 30,
                        height: 30,
                    },
                    num: {
                        width: 16,
                        fontSize: 16,
                        fontWeight: 800,
                    },
                    name: {
                        width: nameWidth,
                        fontSize: 16,
                    },
                },
            },
            data: topName,
        },
        // 总数
        {
            type: 'category',
            inverse: true,
            axisTick: 'none',
            axisLine: 'none',
            show: true,
            axisLabel: {
                textStyle: {
                    color: '#1F4265',
                    fontSize: 20,
                },
                formatter: (val) => {
                    return `{num|${val}}{unit|(${unit})}`;
                },
                rich: {
                    num: {
                        fontSize: 16,
                        fontWeight: 600,
                    },
                    unit: {
                        fontSize: 14,
                    },
                },
            },
            data: attaVal,
        },
    ],
    series: [
        // 科研费
        {
            name: '科研费',
            stack: 'total',
            type: 'bar',
            barWidth: barWidth,
            animationDuration: 1500,
            data: reseachData,
            align: 'center',
            itemStyle: {
                normal: { color: '#048AFC', barBorderRadius: barWidth },
            },
            label: {
                show: false,
            },
        },
        // 订购费
        {
            name: '订购费',
            type: 'bar',
            barWidth: barWidth,
            stack: 'total',
            data: orderData,
            itemStyle: {
                normal: {
                    barBorderRadius: barWidth,
                    color: '#FDDF03',
                },
                // emphasis: {
                //     color: 'rgba(165, 213, 232, 1)',
                // },
            },
        },
        // 维修费
        {
            name: '维修费',
            type: 'bar',
            barWidth: barWidth,
            stack: 'total',
            data: repairData,
            itemStyle: {
                normal: {
                    barBorderRadius: barWidth,
                    color: '#F98519',
                },
                // emphasis: {
                //     color: 'rgba(165, 213, 232, 1)',
                // },
            },
        },
    ],
};
