var seriesColor = ["#00baff", "#3de7c9", "#f5222d", "#b96bc6"];
var legendData = [];
var xaxisList = [];
var metaDate = [];
var serieData = [];

for (var i = 0; i < dataInfo.length; i++) {
    legendData.push(dataInfo[i].name);
    var arrGroup = [];
    for (var j = 0; j < dataInfo[i].count.length; j++) {
        arrGroup.push(dataInfo[i].count[j].total);
    }
    metaDate.push(arrGroup);
}
for (var k = 0; k < dataInfo[0].count.length; k++) {
    xaxisList.push(dataInfo[0].count[k].point);
}
// console.log(legendData)
// console.log(xaxisList)
// console.log(metaDate)

for (var v = 0; v < legendData.length; v++) {
    var serie = {
        name: legendData[v],
        type: "line",
        symbol: "circle",
        symbolSize: 10,
        itemStyle: {
            normal: {
                color: seriesColor[v]
            }
        }, //拐点样式
        lineStyle: {
            // 系列级个性化折线样式
            width: 1,
            type: "solid",
            color: seriesColor[v]
        },

        data: metaDate[v]
    };
    serieData.push(serie);
}
var option = {
    backgroundColor: '#0a2a43',
    tooltip: {
        trigger: "axis" //鼠标经过提示
    },
    legend: {
        icon: "rect", //icon为圆角矩形
        top: "0",
        right: "0",
        itemWidth: 20,
        itemHeight: 20,
        data: legendData,
        textStyle: {
            //图例文字的样式
            color: "#ffffff",
            fontSize: 15
        }
    },
    grid: {
        left: 0,
        top: "10%",
        right: "1%",
        bottom: "10%",
        containLabel: true
    },
    xAxis: {
        type: "category",
        boundaryGap: false,
        data: xaxisList,
        axisLine: {
            lineStyle: {
                color: "#525e70",
                width: 1
            }
        },
        axisLabel: {
            interval: 0,
            color: "#9da3ad"
        },
        axisTick: {
            show: false
        }
    },
    yAxis: {
        name: "人",
        nameGap: 8,
        type: "value",
        splitLine: {
