        // if (searchedValue &&  node.name === searchedValue) {
        
        // 模糊匹配
        if (searchedValue && node.name.toLowerCase().indexOf(searchedValue.toLowerCase()) > -1) {
            node.itemStyle = {
                color: 'black'
            };
        }
    });

    data.links.forEach(link => {
        link.label = {
            align: 'center',
            fontSize: 12
        };

        if (link.name === '') {
            link.lineStyle = {
                color: color2
            }
        } else if (link.name === '') {
            link.lineStyle = {
                color: color1
            }
        } else if (link.name === '') {
            link.lineStyle = {
                color: color3
            }
        }
    });

    let categories = [{
            name: '华东',
            itemStyle: {
                color: color1
            }
        },
        {
            name: '华南',
            itemStyle: {
                color: color2
            }
        },
        {
            name: '西南',
            itemStyle: {
                color: color3
            }
        },
        {
            name: '华北',
            itemStyle: {
                color: color3
            }
        }
    ]

    option = {
        title: {
            text: '关系图谱',
        },
        legend: [{
            // selectedMode: 'single',
            data: categories.map(x => x.name),
            // icon: 'circle'
        }],
        series: [{
            type: 'graph',
            layout: 'force',
            symbolSize: 58,
            draggable: true,
            roam: true,
            focusNodeAdjacency: true,
            categories: categories,
            edgeSymbol: ['', 'arrow'],
            // edgeSymbolSize: [80, 10],
            edgeLabel: {
                normal: {
                    show: false,
                    textStyle: {
                        fontSize: 20
                    },
                    formatter(x) {
                        return x.data.name;
                    }
                }
            },
            label: {
                show: false,
            },
            force: {
                repulsion: 2000,
                edgeLength: 120
            },
            data: data.nodes,
            links: data.links
        }]
    }
    // 第四处：
    myChart.setOption(option);
}


// 第五处
init();
