        type: 'value'
    },
    series: [{
        data: data,
        label: {
            normal: {
                show: true,
                position: 'top',
                distance: 10,
                // formatter: function(params) {
                //     console.log(params)
                //     return params.data == 0 ? '未提交' : params.data
                // }
            }
        },
        markPoint: {
