// 提取Y轴的数组
var data_categories = [];
for(var i=0; i<data_chinese.length; i++){
	data_categories[i] = data_chinese[i][0];
}

function renderItem(params, api) {
	var categoryIndex = api.value(0);
	var start = api.coord([api.value(1), categoryIndex]);
	var end = api.coord([api.value(2), categoryIndex]);
	var height = api.size([0, 1])[1] * 0.6;

	var rectShape = echarts.graphic.clipRectByRect({
		x: start[0],
		y: start[1] - height / 2,
		width: end[0] - start[0],
		height: height
	}, {
		x: params.coordSys.x,
		y: params.coordSys.y,
		width: params.coordSys.width,
		height: params.coordSys.height
	});

	return rectShape && {
		type: 'rect',
		shape: rectShape,
		style: api.style()
	};
}

option = {

	// 设置图表的标题
	title: {
		text: '中国朝代更替图',
		left: 'left'
	},

	// 设置浮动提示框的内容
	tooltip: {
        axisPointer: {
            type: 'cross'
        },
		formatter: function (params) {
			var params_return = "";
			params_return += params.marker;
			params_return += params.name;
			params_return += '<br/>主要人物: '+ params.value[3];
			params_return += '<br/>主要地区: '+ params.value[4];
			params_return += '<br/>开始时间: ';
			if(params.value[1] < 0){ params_return += '公元前'+ Math.abs(params.value[1]) +'年'; }else{ params_return += Math.abs(params.value[1]) +'年'; }
			params_return += '<br/>结束时间: ';
			if(params.value[2] < 0){ params_return += '公元前'+ Math.abs(params.value[2]) +'年'; }else{ params_return += Math.abs(params.value[2]) +'年'; }
			return params_return; 
		}
	},

	// 设置X轴与Y轴的放大器
	dataZoom: [{
		yAxisIndex: [0],
		type: 'slider',
		show: true,
		realtime: true,
		start: 0,
		end: 20,
		left: '1%',
	},{
		xAxisIndex: [0],
		type: 'slider',
		show: true,
		realtime: true,
		start: 0,
		end: 100,
		bottom: '5%',
	}],

	// 设置图表外边框距
	grid: {
		top: '5%',
		bottom: '15%',
		left: '13%',
		right: '3%',
	},

	// 设置X轴的参数
	xAxis: {
		name: '年',
		splitNumber: 10,
		min: time_min,
		max: time_max,
		splitLine: {
			show: true
		},
        axisLabel: {
            formatter: function (val) {
				if(val < 0){ return 'BC'+ Math.abs(val) }else{ return Math.abs(val) }
            },
        },
	},

	// 设置Y轴的参数
	yAxis: {
		data: data_categories,
		scale: true,
		splitLine: {
			show: true
		}
	},

	// 设置图表内容
	series: [{
		type: 'custom',
		renderItem: renderItem,
		itemStyle: {
			normal: {
				opacity: 0.8
			}
		},
		encode: {
			x: [1, 2],
			y: 0
		},
		data: data_chinese,
	}],
};
