//数据
var data = [{
        "name": "IT人员",
        "value": 300
    },
    {
        "name": "医务人员",
        "value": 106
    },
    {
        "name": "教师工作人员",
        "value": 210
    },
    {
        "name": "精神病障碍患者",
        "value": 320
    },
    {
        "name": "快递人员",
        "value": 490
    },
    {
        "name": "银行人员",
        "value": 150
    },

    {
        "name": "犀利的危险人员",
        "value": 302
    }
];
//图片
var img = "data:image/png;base64,";
//控制每行字符个数

var xData = [],
    yData = [],
    Datas = [];
data.map(function(a, b) {
    xData.push(a.name);
    yData.push(a.value);
    Datas.push([a.name, a.value])
    // if (a.value === 0) {
    //     yData.push(a.value + min);
    // } else {
    //     yData.push(a.value);
    // }
    // yData.push((Math.random(0,1) * 100).toFixed(0));
});
//找出最大值，判断缩放比
let max = 0;
let len = data.length;
let scale = 1;
for (let i = 0; i < len; i++) {
    if (data[i].value > max) {
        max = data[i].value;
    }
}
if (max > 0 && max < 100) {
    scale = 0.2;
} else if (max > 100 && max < 500) {
    scale = 0.3;
} else if (max > 500 && max < 1000) {
    scale = 0.5;
} else if (max > 1000 && max < 5000) {
    scale = 2;
} else if (max > 5000) {
    scale = 5;
}
var option = {
    tooltip: {},
    backgroundColor: '#0e2147',
    grid: {
        left: '8%',
        bottom: '26%',
        top: '20%',
        width: '89%'
    },
    xAxis: {
        type: 'category',
        data: xData,
        splitLine: {
            show: false
        }, //去除网格线
        axisTick: {
            alignWithLabel: true
        },
        axisLine: {
            lineStyle: {
                color: '#4073b2',
                width: 2,
            }
        },
        axisLabel: {
            interval: 0,
            textStyle: {
                fontSize: 14,
                color: '#fff',
            },
            formatter: function(value) {
                var ret = ""; //拼接加\n返回的类目项
                var maxLength = 3; //每项显示文字个数
                var valLength = value.length; //X轴类目项的文字个数
                var rowN = Math.ceil(valLength / maxLength); //类目项需要换行的行数
                if (rowN > 1) //如果类目项的文字大于3,
                {
                    for (var i = 0; i < rowN; i++) {
                        var temp = ""; //每次截取的字符串
                        var start = i * maxLength; //开始截取的位置
                        var end = start + maxLength; //结束截取的位置
                        //这里也可以加一个是否是最后一行的判断，但是不加也没有影响，那就不加吧
                        temp = value.substring(start, end) + "\n";
                        ret += temp; //凭借最终的字符串
                    }
                    return ret;
                } else {
                    return value;
                }
            }
        }
    },
    yAxis: {
        type: 'value',
        name: '人数',
        nameTextStyle: {
            color: "#fff",
            fontSize: 14,
            margin: 2,
        },
        splitLine: {
            show: false
        }, //去除网格线
        axisLine: {
            lineStyle: {
                color: '#4073b2',
                width: 2,
            }
        },
        axisLabel: {
            formatter: '{value}',
            textStyle: {
                fontSize: 12,
                color: '#fff'
            }
        }
    },
    series: [{
        name: '',
        type: 'scatter',
        data: Datas,
        symbol: `image://${img}`,
        symbolSize: function(data) {
            // console.log('sadf1211221',data[1]);
            if (data[1] < 5) {
                return Math.sqrt(data[1]) / 0.17;
            }
            return Math.sqrt(data[1]) / scale;
        },
        animationDurationUpdate: 8000,
        animationEasingUpdate: 8000,
        animationDelay: function(idx) {
            // 越往后的数据延迟越大
            return idx * 400;
        },
        label: {
            normal: {
                show: true,
                position: 'top',
                // distance: 16,
                formatter: function(params) {
                    return params.value[1]
                },
                color: '#fff',
                fontSize: 14
            }

        }
    }]
};
