let _data = [-10, -9, -8, -7, -6, -5, -4, -3, -2, 5];
let trueData = _data.map((v) => Math.abs(v));
// 红的
let imgUp =
    'data:image/png;base64,';
// 绿的
let imgDown =
    'data:image/png;base64,';
const colors = [
    '#377D2C',
    '#CCCCCC',
    '#CCCCCC',
    '#CCCCCC',
    '#CCCCCC',
    '#CCCCCC',
    '#CCCCCC',
    '#F7CA75',
    '#F7CA75',
    '#F7CA75',
];

option = {
    backgroundColor: '#fff',
    tooltip: {
        trigger: 'axis',
        show: true,
        axisPointer: {
            // 坐标轴指示器，坐标轴触发有效
            type: 'shadow', // 默认为直线，可选为：'line' | 'shadow'
        },
        formatter: function (parmas, i) {
            return _data[parmas[0].dataIndex] > 0
                ? parmas[0].name + ' : ' + parmas[0].value
                : parmas[0].name + ' : -' + parmas[0].value;
        },
    },
    legend: {
        show: false,
    },
    grid: {
        left: '-10%',
        right: '10%',
        bottom: '3%',
        containLabel: true,
    },
    xAxis: [
        {
            splitLine: {
                show: false,
            },
            type: 'value',
            show: false,
        },
    ],
    yAxis: [
        {
            splitLine: {
                show: false,
            },
            axisLine: {
                //y轴
                show: false,
            },
            type: 'category',
            axisTick: {
                show: false,
            },
            inverse: true,
            data: [
                'OCBG 滤片',
                'OPBG ALPS',
                'OCBG 楼镜线',
                'OCBG 单反线',
                'OPBG 米兰线',
                'OPBG 光学',
                'OCBG 镀膜线',
                'BMBG 品园线',
                'OCBG 滤片线',
                'OPBG 滤线Cover',
            ],
            axisLabel: {
                color: '#aaa',
                fontSize: 14,
                margin: 145,
                textStyle: {
                    align: 'left',
                },
            },
        },
        // {
        //     type: 'category',
        //     inverse: true,
        //     offset: 10,
        //     axisTick: 'none',
        //     axisLine: 'none',
        //     show: true,
        //     data: _data,
        //     axisLabel: {
        //         textStyle: {
        //             color: '#000',
        //             fontSize: '12',
        //         },
        //     },
        // },
        {
            type: 'category',
            inverse: true,
            offset: 60,
            axisTick: 'none',
            axisLine: 'none',
            show: true,
            axisLabel: {
                textStyle: {
                    color: '#000',
                    fontSize: '12',
                },
                formatter: (param) => {
                    let index;
                    if (param > 0) {
                        index = 1;
                    } else {
                        index = 2;
                    }
                    return `{img${index}|}{value|${param}%}`;
                },
                rich: {
                    img1: {
                        height: 25,
                        backgroundColor: { image: imgUp },
                    },
                    img2: {
                        height: 25,
                        backgroundColor: { image: imgDown },
                    },
                },
            },
            data: _data,
        },
    ],
    series: [
        {
            name: '',
            type: 'bar',
            barWidth: 25, // 柱子宽度
            label: {
                show: false,
                position: 'right', // 位置
                color: '#000',
                fontSize: 24,
                distance: 12, // 距离
                formatter: function (value, i) {
                    return value[i];
                },
            }, // 柱子上方的数值
            itemStyle: {
                barBorderRadius: [0, 0, 0, 0], // 圆角（左上、右上、右下、左下）
                normal: {
                    color: function (params) {
                        return colors[params.dataIndex];
                        // 取每条数据的 index 对应 colors 中的 index 返回这个颜色
                    },
                },
            },
            data: trueData,
        },
    ],
};
