const title = '总量'
const color = ['#5E944A', '#C2A349', '#54B4DC', '#1D48BB', '#1C89FA']
const formatNumber = function (num) {
  const reg = /(?=(\B)(\d{3})+$)/g
  return num.toString().replace(reg, ',')
}
const chartIcon = [
   'data:image/png;base64,',
   'data:image/png;base64,',
   'data:image/png;base64,',
   'data:image/png;base64,',
   'data:image/png;base64,'
]
const echartData = [{
  name: '无通行证闯禁',
  value: '256'
},
{
  name: '不按规定路线行驶',
  value: '212'
},
{
  name: '不按规定时间行驶',
  value: '231'
},
{
  name: '高速右转',
  value: '222'
},
{
  name: '疲劳驾驶',
  value: '244'
}
]
const total = function () {
    let num = 0
    echartData.forEach(item => {
        num =num + (+item.value)
    })
    return num
}
option = {
    color: color,
    tooltip: {
      trigger: 'item'
    },
    title: [{
      text: '{val|' + formatNumber(total()) + '}\n{name|' + title + '}',
      top: '40%',
      left: '40%',
      textAlign: 'center',
      textStyle: {
        rich: {
          val: {
            fontSize: 24,
            color: '#3196FA',
