      formatter: function (name) {
        let str = ''
        let res = echartData.filter(v => v.name === name)
        res = res[0] || {}
        switch (name) {
          case '无通行证闯禁':
            str = '{a|}{name|' + name + ' }{value|' + res.value + '}{unit|次}'
            break;
