            },
            align: 'left'
          },
          d: {
            width: 20,
            height: 20,
            lineHeight: 20,
            backgroundColor: {
              image: chartIcon[3]
            },
            align: 'left'
          },
          e: {
            width: 20,
            height: 20,
            lineHeight: 20,
            backgroundColor: {
              image: chartIcon[4]
            },
            align: 'left'
          },
          name: {
            fontSize: 12,
            padding: [0, 0, 0, 5],
            width: 100
