data.code:
  var data = {
      data: [
          25,
          35,
          45,
          55,
          65,
          75,
          85,
         100
      ],
      total: 100,
      leftname: [
          "张三",
          "李四",
          "王五",
          "赵六",
          "孙七",
          "周八",
          "吴九",
          "郑十"
      ]
  };

  var mydata = [];
  var totaldata = [];
  var percentdata = [];
  $.each(data.data, function (key, value) {
      totaldata.push(100);
      mydata.push(100 * value / 100);
      percentdata.push(Math.round(value / data.total * 100) + "%");
  });
  var fillImg = 'data:image/png;base64,';
  var option = {
      backgroundColor: '#000000',
      grid: {
          top: '5%',
          left: '10%',
          bottom: 0
      },
      xAxis: [{
          show: false,
      }],
      yAxis: [
          {
              axisTick: 'none',
              axisLine: 'none',
              offset: '10',
              axisLabel: {
                  margin: 20,
                  textStyle: {
                      color: '#8aa3b0',
                      fontSize: '14',
                  }
              },
              data: data.leftname
          },
          {
              axisTick: 'none',
              axisLine: 'none',
              type: 'category',
              axisLabel: {
                  margin: 20,
                  textStyle: {
                      color: '#d9dddf',
                      fontSize: '16',
                  }
              },
              data: percentdata,
          },
          {
              name: '',
              nameGap: '50',
              nameTextStyle: {
                  color: '#000',
                  fontSize: '16',
              },
              axisLine: {
                  lineStyle: {
                      color: 'rgba(0,0,0,0)'
                  }
              },
              data: [],
          }
      ],
      series: [
          {
              name: '', // blue bar
              type: 'pictorialBar',
              symbol: 'image://' + fillImg,
              barWidth: '100%',
              symbolOffset: [5, 0],
              itemStyle: {
                  normal: {
                      barBorderRadius: 5,
                      color: '#6DE8FA'
                  }
              },
              symbolSize: ['100%', 13],
              symbolBoundingData: 99,
              symbolClip: true,
              data: mydata,
              label: {
                  normal: {
                      show: false,
                  }
              },
              animationEasing: 'elasticOut',
          },
          {
              name: '外框',
              type: 'bar',
              yAxisIndex: 2,
              barGap: '-100%',
              data: totaldata,
              barWidth: 20,
              // barCategoryGap:'200',
              itemStyle: {
                  normal: {
                      color: '#131a27', //填充色
                      barBorderColor: '#c7cad0', //边框色
                      barBorderWidth: 1, //边框宽度
                      barBorderRadius: 0, //圆角半径
                      label: { //标签显示位置
                          show: false,
                          position: 'top' //insideTop 或者横向的 insideLeft
                      }
                  }
              },
              z: 0
          }
      ]
  };
