var option = getOption(data[0], nameArr);
//响应legend点击事件
myChart.on('legendselectchanged', function(param) {
   let name = param.name;
   let selected = {};
   let selIdx = 0;
   for(var i = 0; i < nameArr.length; i++) {
       if(name == nameArr[i]) {
           selected[nameArr[i]] = true;
           selIdx = i;
       } else {
           selected[nameArr[i]] = false;
       }
   }
   option = getOption(data[selIdx], nameArr);
   option.legend.selected = selected;
   myChart.setOption(option, true);
});

// 定时触发点击legend事件
let idx = 0;
let timer = setInterval(function() {
    myChart.dispatchAction(
        { type: 'legendToggleSelect', 
          name: nameArr[++idx % nameArr.length] }
    )
}, 2000);

// 鼠标移动到地图部分停止自动切换
$('#chart-panel').on("mouseover", function() {
   clearInterval(timer);
});


function getOption(itemData, nameArr) {
    //alert(JSON.stringify(itemData));
    option = {
        title: {
            text: itemData.title + '销量',
            subtext: '纯属虚构',
            left: 'left'
        },
        tooltip: {
            trigger: 'item'
        },
        color: ['#ff0000', '#ff0000', '#ff0000'],
        legend: {
           // orient: 'vertical',
            left: 'center',
            selectedMode: 'single',
            selected: {},
            data: nameArr
        },
        visualMap: {
            min: 0,
            max: 2500,
            left: 'left',
            top: 'bottom',
            text: ['高','低'],           // 文本，默认为数值文本
            calculable: true
        },
        toolbox: {
            show: true,
            orient: 'vertical',
            left: 'right',
            top: 'center',
            feature: {
                dataView: {readOnly: false},
                restore: {},
                saveAsImage: {}
            }
        },
        series: [
            {
                name: '销量',
                type: 'map',
                mapType: 'china',
                showLegendSymbol: false,
                roam: false,
                label: {
                    normal: {
                        show: true
                    },
                    emphasis: {
                        show: true
                    }
                },
                data: itemData.data
            },
            {
                name: nameArr[0],
                type: 'map',
                mapType: 'china',
                label: {
