const datas = [10, 23, 55, 67];
function max(datas) {
    var max = datas[0];
    for (var i = 0; i < datas.length; i++) {
        if (max < datas[i]) {
            max = datas[i];
        }
    }
    return max;
}
// 背景xdata  渐变色柱状图   间隔色 两种  你可以设置多种
var bgData = [];
for (var i = 0; i < datas.length; i++) {
    bgData.push(max(datas) + 40);
}
getSymbolData = (datas) => {
    let arr = [];
    for (var i = 0; i < datas.length; i++) {
        arr.push({
            value: datas[i],
            symbolPosition: 'end',
        });
    }
    return arr;
};
