function _pie1() {
    let dataArr = [];
    for (var i = 0; i < 8; i++) {

        dataArr.push({
            name: (i + 1).toString(),
            value: 20,
            itemStyle: {
                normal: {
                    color: "rgba(88,142,197,0.1)",
                    borderWidth: 0,
                    borderColor: "rgba(0,0,0,0)"
                }
            }
        })

    }
    return dataArr

}

function _pie2() {
    let dataArr = [];
    var index = 0;

    for (var i = 0; i < 8; i++) {
        if (i % 2 === 0) {

            var temp = new echarts.graphic.LinearGradient(
                0, 0, 1, 1,
                [{
                        offset: 0,
                        color: '#3ac26e'
                    },
                    {
                        offset: 0.2,
                        color: '#dab344'
                    },
                    {
                        offset: 0.4,
                        color: '#0CB9FF'
                    },
                    {
                        offset: 0.6,
                        color: '#e72b7c'
                    },
                    {
                        offset: 0.8,
                        color: '#1976b9'
                    },
                    {
                        offset: 1,
                        color: '#620fa0'
                    }
                ]
            )

            dataArr.push({
                name: (i + 1).toString(),
                value: 25,
                itemStyle: {
                    normal: { //颜色渐变
                        color: temp,
                        borderWidth: 0,
                        borderColor: "rgba(0,0,0,0)"
                    }
                }
            })
        } else {
            dataArr.push({
                name: (i + 1).toString(),
                value: 5,
                itemStyle: {
                    normal: {
                        color: "rgba(0,0,0,0)",
                        borderWidth: 0,
                        borderColor: "rgba(0,0,0,0)"
                    }
                }
            })
        }

    }
    return dataArr

}
