/*
报到状态 isCheckIn
注册状态 isRegistered
缴费状态 isPay
*/

data = {
    正常: [
        ['2019', 'up', '2019-1', {
            isCheckIn: 0,
            isRegistered: 0,
            isPay: 0,
        }],
        ['2019', 'down', '2019-2', {
            isCheckIn: 0,
            isRegistered: 0,
            isPay: 0,
        }],
    ],
    异常: [
        ['2020', 'up', '2020-1', {
            isCheckIn: 0,
            isRegistered: 1,
            isPay: 1,
        }],
    ],
    未开始: [
        ['2020', 'down', '2020-2', {
            isCheckIn: 2,
            isRegistered: 2,
            isPay: 2,
        }],
        ['2021', 'up', '2021-1', {
            isCheckIn: 2,
            isRegistered: 2,
            isPay: 2,
        }],
        ['2021', 'down', '2021-2', {
            isCheckIn: 2,
            isRegistered: 2,
            isPay: 2,
        }],
        ['2022', 'up', '2022-1', {
            isCheckIn: 2,
            isRegistered: 2,
            isPay: 2,
        }],
        ['2022', 'down', '2022-2', {
            isCheckIn: 2,
            isRegistered: 2,
            isPay: 2,
        }],
    ]
}


xdata = [2019, 2020, 2021, 2022]


var markPointData = []

for (var i = 0; i < xdata.length; i++) {
    var item = {
        name: i,
        coord: [i, 'middle']
    }
    markPointData.push(item)
}


var red = 'data:image/png;base64,'
var gray = 'data:image/png;base64,'
var green = 'data:image/png;base64,'


CheckInList = ['已报到', '未报到', '未开始']
RegisteredList = ['已注册', '未注册', '未开始']
PayList = ['已缴费', '欠费（住宿费未缴纳）', '未开始']



colorArry = [
    new echarts.graphic.LinearGradient(1, 0, 0, 0, [{
            offset: 0,
            color: "#0FB27C"
        },
        {
            offset: 1,
            color: "#4BCDA1"
        }
    ]),
    new echarts.graphic.LinearGradient(1, 0, 0, 0, [{
            offset: 0,
            color: "#FF6061"
        },
        {
            offset: 1,
            color: "#FF7E7C"
        }
    ]),
    '#D8D8D8'
]

let markLineColor = {
    type: 'linear',
    x: 1,
    y: 0,
    x2: 0,
    y2: 0,
    colorStops: [{
        offset: 0,
        color: 'rgba(32, 200, 144, 0.98)'
    }, {
        offset: 1,
        color: 'rgba(32, 200, 144, 0.11)',
    }]
}

let markPointColor = {
    type: 'linear',
    x: 1,
    y: 0,
    x2: 0,
    y2: 0,
    colorStops: [{
        offset: 0,
        color: 'rgba(32, 200, 144, 0.98)'
    }, {
        offset: 1,
        color: 'rgba(32, 200, 144, 0.11)',
    }]
}


option = {
    color: colorArry,
    tooltip: {
        backgroundColor: '#fff',
        borderWidth: .5,
        borderColor: 'rgba(0, 0, 0, 0.1)',
        padding: 10,
        formatter: function(parme) {
            const {
                isCheckIn,
                isPay,
                isRegistered
            } = parme.data[3]
            var CheckIn = CheckInList[isCheckIn]
            var Pay = PayList[isPay]
            var Registered = RegisteredList[isRegistered]
            if (isCheckIn === 0) {
                CheckIn = '<span style="color:#10AF7A;">' + CheckIn + '</span>'
            } else if (isCheckIn === 1) {
                CheckIn = '<span style="color:#FF5555;">' + CheckIn + '</span>'
            } else {
                CheckIn = CheckIn
            }

            if (isPay === 0) {
                Pay = '<span style="color:#10AF7A;">' + Pay + '</span>'
            } else if (isPay === 1) {
                Pay = '<span style="color:#FF5555;">' + Pay + '</span>'
            } else {
                Pay = Pay
            }

            if (isRegistered === 0) {
                Registered = '<span style="color:#10AF7A;">' + Registered + '</span>'
            } else if (isRegistered === 1) {
                Registered = '<span style="color:#FF5555;">' + Registered + '</span>'
            } else {
                Registered = Registered
            }

            return '报到状态：' + CheckIn + '</br>' + '注册状态：' + Registered +
                '</br>' + '缴费状态：' + Pay
        },
        textStyle: {
            color: '#4E4E4E'
        }
    },
    backgroundColor: '#F4FBFB',
    legend: {
        data: ['正常', '异常', '未开始'


        ],
        itemWidth: 12,
        itemHeight: 12,
        icon: 'roundRect',
        right: 0,
        top: 0,
    },

    xAxis: {
        show: false,
        data: xdata
    },
    yAxis: {
        show: false,
        inverse: true,
        data: ['up', 'middle', 'down'],
    },
    series: [{
            name: '正常',
            type: 'scatter',
            symbol: 'image://' + green, //'circle', 'rect', 'roundRect', 'triangle', 'diamond', 'pin', 'arrow'
            symbolSize: [85, 30],
            label: {
                show: true,
                position: 'inside',
                formatter: function(param) {
                    return param.data[2];
                },
                color: '#fff',
                fontSize: 16,
                fontWeight: 'bold',
                verticalAlign: 'middle',
                // borderRadius: 4,
                // backgroundColor: '#0FB27C',
                padding: [10, 12]
            },
            itemStyle: {
                normal: {
                    color: colorArry[0],
                },
                emphasis: {
                    scale: false
                }
            },
            data: data['正常'],

            // silent: true,

        },
        {
            name: '异常',
            type: 'scatter',
            symbol: 'image://' + red, //'circle', 'rect', 'roundRect', 'triangle', 'diamond', 'pin', 'arrow'
            symbolSize: [85, 30],
            label: {
                show: true,
                position: 'inside',
                formatter: function(param) {
                    return param.data[2];
                },
                color: '#fff',
                fontSize: 16,
                fontWeight: 'bold',
                verticalAlign: 'middle',
                // borderRadius: 4,
                // backgroundColor: '#FF6061',
                // padding: 10
            },
            itemStyle: {
                normal: {
                    color: colorArry[1],
                }
            },
            data: data['异常'],
            // silent: true,
        },

        {
            name: '未开始',
            type: 'scatter',
            symbol: 'image://' + gray, //'circle', 'rect', 'roundRect', 'triangle', 'diamond', 'pin', 'arrow'
            symbolSize: [85, 30],
            label: {
                show: true,
                position: 'inside',
                formatter: function(param) {
                    return param.data[2];
                },
                color: '#fff',
                fontSize: 16,
                fontWeight: 'bold',
                verticalAlign: 'middle',
                // borderRadius: 4,
                // backgroundColor: '#D8D8D8',
                // padding: 10
            },
            itemStyle: {
                normal: {
                    color: colorArry[2],
                }
            },
            data: data['未开始'],
            // silent: true,
        },
        // 模拟中线
        {
            name: '',
            type: 'scatter',
            symbol: 'rect', //'circle', 'rect', 'roundRect', 'triangle', 'diamond', 'pin', 'arrow'
            symbolSize: 0,
            label: {
                show: false,
                position: 'inside',
                formatter: function(param) {
                    return param.data[2];
                },
                fontSize: 16,
                fontWeight: 'bold',
                verticalAlign: 'middle',
                borderRadius: 4,
                backgroundColor: '#0FB27C',
                padding: 10
            },
            markPoint: {
                data: markPointData,
                symbol: "circle",
                symbolSize: 10,
                itemStyle: {
                    shadowColor: '#13B881',
                    shadowBlur: 10,
                    color: '#13B881'
                },
                tooltip: {
                    show: false
                }
            },
            markLine: {
                symbol: ['none', 'triangle'],
                symbolSize: [10, 10],
                label: {
                    show: false
                },
                data: [{
                    type: "average"
                }],
                lineStyle: {
                    type: "solid",
                    width: 4,
                    color: markLineColor
                },
                tooltip: {
                    show: false
                }
            },
            itemStyle: {
                normal: {
                    color: 'rgba(32, 200, 144, 0)',
                }
            },
            data: data['正常'],
        },

    ]
};
