data.code:
var bgImg = 'data:image/png;base64,';
var fillImg = 'data:image/png;base64,';


var data = [{
    name: '初级',
    value: 2154
}, {
    name: '中级',
    value: 3000
}, {
    name: '高级',
    value: 2357
}];
var maxData = [];
var maxNumArray = [];
data.forEach(function(value, index) {
    maxNumArray.push(value.value);
})
data.forEach(function(value, index) {
    var num;
    maxData.push({
        name: value.name,
        value: Math.max.apply(null, maxNumArray)
    });
})
var scale = 1;
//富文本配置
var rich = {
    white: {
        color: "#fff",
        align: 'left',
        fontSize: 18 * scale,
        padding: [0, 0]
    },
};
var option = {
    backgroundColor: '#031f2d',
    grid: {
        left: '3%',
        right: '6%',
        bottom: '3%',
        containLabel: true
    },
    yAxis: [{
        type: 'value',
        splitLine: {
            show: false
        },
        axisLine: {
            // show: false,
            lineStyle: {
                color: '#0b5263'
            }
        },
        axisTick: {
            show: true,
            inside: true,
            length: 10 * scale
        },
        axisLabel: {
            textStyle: {
                fontSize: 14 * scale
            }
        },
    }],
    xAxis: [{
        type: 'category',
        data: ['初级', '中级', '高级'],
        boundaryGap: ['20%', '20%'],
        splitLine: {
            show: false
        },
        axisLine: {
            // show: false,
            lineStyle: {
                color: '#0b5263'
            }
        },
        axisTick: {
            show: false
        },
        axisLabel: {
            //   rotate: 45,
            padding: [15, 0, 0, 0],
            textStyle: {
                fontSize: 16 * scale,
                color: '#3fdaff'
            }
        }
    }],
    series: [{
        name: '100',
        type: 'pictorialBar',
        stack: '总量',
        z: 3,
        barWidth: '45%',
        data: data,
        symbol: 'image://'+ fillImg,
        symbolClip: true,
            symbolSize: [52, '100%']
    }, {
        name: '占位',
        type: 'pictorialBar',
        barWidth: '45%',
        barGap: '-100%',
        silent: true,
        label: {
            normal: {
                show: true,
                position: "top",
                distance: 20 * scale,
                formatter: function(params) {
                    var stuNum = 0;
                    data.forEach(function(value, index, array) {
                        if (params.name == value.name) {
                            stuNum = value.value;
                        }
                    })
                    return stuNum + '{white| 人}';
                },
                textStyle: {
                    color: "#ffc72b",
                    fontSize: 20 * scale
                },
                rich: rich
            }
        },
        symbol: 'image://'+ bgImg,
        symbolClip: true,
        symbolSize: [52, '100%'],
        data: maxData
    }]
};
