                        lineStyle:{
                            width:3,
                            color: '#0ff'
                        },
                        areaStyle:{
                            normal:{
                                color: 'rgba(2, 159, 254, 0.7)'
                            },
                        }
                    }, {
                        name: '企业用户',
                        value : [1300, 11000, 21000, 35000],
                        lineStyle: {
                            normal: {
                                color: "#F8E81C"
                            }
                        },
                        areaStyle: {
                            normal: {
                                color: 'rgba(248,230,27,0.21)'
                            }
                        }
                    }
                ],
            }]

        }
