data.code:
	var icon = "data:image/png;base64,"
	var ShouldAttendNumber = [100, 100, 100, 100, 100, 100, 100]
	option = {
	    backgroundColor: "#C0C0C0",
	    title: {
	        text: this.title,
	        top: "3%",
	        left: "25px",
	        textStyle: {
	            fontSize: 14,
	            color: "#F7F7F7",
	            fontWeight: "normal"
	        }
	    },
	    tooltip: {
	        trigger: 'axis'
	    },
	    legend: {
	        data: ['应出勤', '实际出勤'],
	        top: '3%',
	        right: '4%',
	        textStyle: {
	            fontSize: '14px', // 字体大小
	            color: '#F7F7F7'
	        }

	    },
	    grid: {
	        top: '25%',
	        left: '3%',
	        right: '4%',
	        bottom: '3%',
	        containLabel: true
	    },
	    xAxis: {
	        type: 'category',
	        data: ['1日', '2日', '3日', '4日', '5日', '6日', '7日'],
	        axisLabel: {
	            color: 'white'
	        },
	        axisTick: {
	            show: false
	        },
	        axisLine: {
	            show: false
	        }
	    },
	    yAxis: {
	        axisLabel: {
	            show: false
	        },
	        axisLine: {
	            show: false
	        },
	        splitLine: {
	            show: true,
	            lineStyle: {
	                color: '#064C9E'
	            }
	        }
	    },
	    series: [{
	            name: '实际出勤',
	            type: 'bar',
	            stack: '总量',

	            barWidth: 20,
	            label: {
	                show: true,
	                position: [-10, -30],
	                distance: 0,
	                color: "#fff",
	                formatter: function(val) {
	                    var content = `${(val.data / ShouldAttendNumber[val.dataIndex]) * 100}%`
	                    return content
	                },
	                fontSize: 12,
	                backgroundColor: {
	                    image: icon,
	                    color: "#034B8C"
	                },
	                padding: [5, 8, 8, 8],
	                borderRadius: 100
	            },
	            itemStyle: {
	                normal: {
	                    color: new echarts.graphic.LinearGradient(0, 1, 0, 0, [{
	                        offset: 0,
	                        color: '#4F8316' // 0% 处的颜色
	                    }, {
	                        offset: 1,
	                        color: '#B6FF77' // 60% 处的颜色
	                    }], false),
	                    barBorderRadius: 2
	                }
	            },
	            data: [10, 20, 30, 40, 50, 60, 70]
	        },
	        {
	            name: '应出勤',
	            type: 'bar',
	            z: 1,
	            barGap: '-100%', // 重叠的关键
	            barWidth: 20,
	            label: {
	                show: true,
	                position: [-10, -30],
	                distance: 0,
	                color: '#fff',
	                fontSize: 12,
	                backgroundColor: {
	                    image: icon
	                },
	                padding: [8, 9, 8, 9],
	                borderRadius: 100
	            },
	            itemStyle: {
	                normal: {
	                    color: new echarts.graphic.LinearGradient(0, 1, 0, 0, [{
	                        offset: 0,
	                        color: '#2F34FF' // 0% 处的颜色
	                    }, {
	                        offset: 1,
	                        color: '#584EFF' // 60% 处的颜色
	                    }], false),
	                    barBorderRadius: 2
	                }

	            },
	            data: [100, 100, 100, 100, 100, 100, 100]
	        }
	    ]
	}
