let icon =
    'data:image/png;base64,';

let pointStyle = {
    borderColor: '#ea6f21',
    color: '#fff',
    borderWidth: 2,
}
let labelStyle = {
    show: true,
    position: 'top',
    padding: [10, 15, 15],
    color: '#000000',
    backgroundColor: {
        image: icon,
    },
    fontSize: 13,
    fontWeight: 'normal',
}
let total = [{
        num: 30,
        value: 55
    },
    {
        num: 40,
        value: 65
    },
    {
        num: 20,
        value: 95
    },
    {
        num: 10,
        value: 68
    },
    {
        num: 70,
        value: 43
    },
    {
        num: 100,
        value: 88
    }
]
let seriesData = []
total = total.sort(function(a, b) {
    return a.value - b.value
})
total.forEach((item, index) => {
    let ob = {
        value: item.value,
        num: item.num,
        itemStyle: pointStyle,
        label: labelStyle,
    }
    seriesData.push(ob)
})

option = {
    title: {
        text: 'label添加箭头',
        left: 'left',
    },
    grid: {
        top: '15%',
        left: '8%',
        right: '8%',
        bottom: '15%',
    },
    tooltip: {
        trigger: "axis",
        formatter: function(params) {
            return '分数：'+params[0].value  + '<br>人数：' + params[0].data.num + '人'
        }
    },
    xAxis: [{
        type: 'category',
        axisTick: {
            show: true,
        },
        splitLine: {
            show: false
        },
        data: ['1/1', '1/2', '1/3', '1/4', '1/5', '1/6'],
    }, ],

    yAxis: [{
        type: 'value',
        min: 0,
        max: 100,
        splitNumber: 3,
        axisLine: {
            show: true,
        },
        axisTick: {
            show: true,
        },
        splitLine: {
            show: false
        },
    }, ],
    series: [{
        type: 'line',
        showAllSymbol: true,
        symbol: 'circle',
        symbolSize: 10,
        lineStyle: {
            normal: {
                color: '#ea6f21',
                width: 5,
            },
        },
        areaStyle: {
            normal: {
                color: new echarts.graphic.LinearGradient(
                    0,
                    0,
                    0,
                    1,
                    [{
                            offset: 0,
                            color: 'rgba(236, 169, 44, 1)',
                        },
                        {
                            offset: 1,
                            color: 'rgba(236, 169, 44,0)',
                        },
                    ],
                    false
                ),
            },
        },
        data: seriesData
    }, ],
};
