{
    "tooltip": {
        "show": false
    },
    "xAxis": {
        "show": false
    },
    "yAxis": {
        "show": false
    },
    "series": {
        "type": "custom",
        "renderItem": "function(params, api) {
            var style = api.style();
            var cw = api.getWidth(),ch = api.getHeight(),rw=160,rh=10,av=api.value(2),cv=api.value(3),arw=rw*av/(av+cv),crw=rw*cv/(av+cv),unit=10000,ul='(亿元)',cul='亿';
            console.log(rw)
            return {
                type:'group',
                position:[cw/2,ch/2],
                children:[
                    {
                      type: 'text',
                      style:{
                        fontSize:36,
                        textAlign:'center',
                        textFill:'#424658',
                        textVerticalAlign:'middle',
                        text:(api.value(1)/unit).toFixed(0)
                      },
                      position:[0,-35]
                    },
                    {
                      type: 'text',
                      style:{
                        fontSize:12,
                        textAlign:'center',
                        textVerticalAlign:'middle',
                        textFill:'#999',
                        text:style.text+ul,
                      },
                    },
                    {
                        type:'group',
                        position:[0,30],
                        children:[
                            {
                                type:'group',
                                position:[-rw/2,-rh/2],
                                children:[
                                    {
                                        type: 'rect',
                                        shape:{
                                            width:crw,
                                            height:rh,
                                        },
                                        style:{
                                            fill:'#ff6367'
                                        }
                                    },{
                                        type: 'rect',
                                        shape:{
                                            width:arw,
                                            height:rh,
                                        },
                                        style:{
                                            fill:'#2872eb'
                                        },
                                        position:[crw,0]
                                    }
                                ]
                            },
                            {
                              type: 'text',
                              style:{
                                fontSize:12,
                                textAlign:'right',
                                textFill:'#424658',
                                textVerticalAlign:'middle',
                                text:'-'+(cv/unit).toFixed(0)+cul
                              },
                              position:[-(rw/2+5),0]
                            },
                            {
                              type: 'text',
                              style:{
                                fontSize:12,
                                textAlign:'right',
                                textFill:'#999',
                                textVerticalAlign:'middle',
                                text:api.value(1)+'个项目'
                              },
                              position:[-(rw/2+5),20]
                            },
                            {
                              type: 'text',
                              style:{
                                fontSize:12,
                                textAlign:'left',
                                textFill:'#424658',
                                textVerticalAlign:'middle',
                                text:'+'+(av/unit).toFixed(0)+cul
                              },
                              position:[rw/2+5,0]
                            },
                            {
                              type: 'text',
                              style:{
                                fontSize:12,
                                textAlign:'left',
                                textFill:'#999',
                                textVerticalAlign:'middle',
                                text:api.value(5)+'个项目'
                              },
                              position:[rw/2+5,20]
                            },
                        ]
                    }
                  ]
            }
        }",
        "label": {
            "normal": {
                "show": true,
                "formatter": "function(v){return v.value[0]}"
            }
        },
        "encode": {
            "x": 1,
            "y": 2,
            "itemName": 0
        }
    }
}
